// src/AnalyticsDashboard.jsx
import React, { useState, useEffect } from 'react';

export default function AnalyticsDashboard() {
  const [analytics, setAnalytics] = useState({
    tickets_per_week: [],
    priority_distribution: {},
    avg_resolution_time: 0.0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAnalytics = async () => {
      try {
        // Use the correct backend URL from your logs
        const url = 'http://localhost:5000/api/analytics';
        console.log('Fetching analytics from:', url);

        const response = await fetch(url, {
          method: 'GET',
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
          },
          mode: 'cors'
        });

        console.log('Response status:', response.status);
        console.log('Response ok:', response.ok);

        if (!response.ok) {
          throw new Error(`HTTP ${response.status}: ${response.statusText}`);
        }

        const responseText = await response.text();
        console.log('Raw response text:', responseText);

        if (!responseText || responseText.trim() === '') {
          throw new Error('Empty response from server');
        }

        let parsedData;
        try {
          parsedData = JSON.parse(responseText);
        } catch (parseError) {
          console.error('JSON Parse Error:', parseError);
          console.error('Response was:', responseText);
          throw new Error(`Invalid JSON response: ${parseError.message}`);
        }

        console.log('Parsed analytics data:', parsedData);
        setAnalytics(parsedData);

      } catch (err) {
        console.error('Analytics fetch error:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    const timeoutId = setTimeout(fetchAnalytics, 100);
    return () => clearTimeout(timeoutId);
  }, []);

  const handleBackNavigation = () => {
    if (window.history.length > 1) {
      window.history.back();
    } else {
      window.location.href = '/';
    }
  };

  const handleRetry = () => {
    setLoading(true);
    setError(null);
    window.location.reload();
  };

  // Custom Bar Chart Component
  const CustomBarChart = ({ data, title }) => {
    if (!data || data.length === 0) return <div>No data available</div>;

    const maxValue = Math.max(...data.map(item => item.count));
    const barWidth = Math.max(60, (window.innerWidth - 200) / data.length - 10);

    return (
      <div style={{ padding: '20px', backgroundColor: '#f8f9fa', borderRadius: '8px' }}>
        <h3 style={{ marginBottom: '20px', color: '#495057' }}>{title}</h3>
        <div style={{ 
          display: 'flex', 
          alignItems: 'flex-end', 
          height: '300px',
          borderBottom: '2px solid #dee2e6',
          borderLeft: '2px solid #dee2e6',
          paddingLeft: '10px',
          paddingBottom: '10px'
        }}>
          {data.map((item, index) => {
            const height = (item.count / maxValue) * 250;
            return (
              <div 
                key={index}
                style={{ 
                  display: 'flex', 
                  flexDirection: 'column', 
                  alignItems: 'center',
                  marginRight: '10px'
                }}
              >
                <div style={{
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  marginBottom: '5px'
                }}>
                  <span style={{ 
                    fontSize: '12px', 
                    fontWeight: 'bold',
                    color: '#0088FE'
                  }}>
                    {item.count}
                  </span>
                </div>
                <div
                  style={{
                    width: `${barWidth}px`,
                    height: `${height}px`,
                    backgroundColor: '#0088FE',
                    borderRadius: '4px 4px 0 0',
                    transition: 'all 0.3s ease',
                    cursor: 'pointer',
                    position: 'relative'
                  }}
                  title={`Week ${item.week}: ${item.count} tickets`}
                  onMouseEnter={(e) => {
                    e.target.style.backgroundColor = '#0066CC';
                    e.target.style.transform = 'scale(1.05)';
                  }}
                  onMouseLeave={(e) => {
                    e.target.style.backgroundColor = '#0088FE';
                    e.target.style.transform = 'scale(1)';
                  }}
                />
                <div style={{
                  marginTop: '8px',
                  fontSize: '11px',
                  color: '#6c757d',
                  textAlign: 'center',
                  transform: 'rotate(-45deg)',
                  whiteSpace: 'nowrap'
                }}>
                  {item.week}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    );
  };

  // Custom Pie Chart Component
  const CustomPieChart = ({ data, title }) => {
    if (!data || Object.keys(data).length === 0) return <div>No data available</div>;

    const entries = Object.entries(data);
    const total = entries.reduce((sum, [_, value]) => sum + value, 0);
    const colors = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8'];

    let cumulativeAngle = 0;
    const radius = 80;
    const centerX = 120;
    const centerY = 120;

    return (
      <div style={{ padding: '20px', backgroundColor: '#f8f9fa', borderRadius: '8px' }}>
        <h3 style={{ marginBottom: '20px', color: '#495057' }}>{title}</h3>
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
          <svg width="240" height="240" style={{ overflow: 'visible' }}>
            {entries.map(([name, value], index) => {
              const percentage = (value / total) * 100;
              const angle = (value / total) * 360;
              const startAngle = cumulativeAngle;
              const endAngle = cumulativeAngle + angle;

              // Convert to radians
              const startRad = (startAngle * Math.PI) / 180;
              const endRad = (endAngle * Math.PI) / 180;

              // Calculate arc path
              const x1 = centerX + radius * Math.cos(startRad);
              const y1 = centerY + radius * Math.sin(startRad);
              const x2 = centerX + radius * Math.cos(endRad);
              const y2 = centerY + radius * Math.sin(endRad);

              const largeArcFlag = angle > 180 ? 1 : 0;

              const pathData = [
                `M ${centerX} ${centerY}`,
                `L ${x1} ${y1}`,
                `A ${radius} ${radius} 0 ${largeArcFlag} 1 ${x2} ${y2}`,
                'Z'
              ].join(' ');

              cumulativeAngle += angle;

              return (
                <g key={index}>
                  <path
                    d={pathData}
                    fill={colors[index % colors.length]}
                    stroke="#fff"
                    strokeWidth="2"
                    style={{ cursor: 'pointer' }}
                    onMouseEnter={(e) => {
                      e.target.style.opacity = '0.8';
                      e.target.style.transform = 'scale(1.05)';
                    }}
                    onMouseLeave={(e) => {
                      e.target.style.opacity = '1';
                      e.target.style.transform = 'scale(1)';
                    }}
                  >
                    <title>{`${name}: ${value} (${percentage.toFixed(1)}%)`}</title>
                  </path>
                </g>
              );
            })}
          </svg>

          <div style={{ marginLeft: '30px' }}>
            <h4 style={{ marginBottom: '15px', color: '#495057' }}>Legend</h4>
            {entries.map(([name, value], index) => {
              const percentage = ((value / total) * 100).toFixed(1);
              return (
                <div key={index} style={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  marginBottom: '8px',
                  fontSize: '14px'
                }}>
                  <div style={{
                    width: '16px',
                    height: '16px',
                    backgroundColor: colors[index % colors.length],
                    marginRight: '8px',
                    borderRadius: '2px'
                  }} />
                  <span style={{ color: '#495057' }}>
                    <strong>{name}:</strong> {value} ({percentage}%)
                  </span>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '50vh',
        fontSize: '18px',
        color: '#666'
      }}>
        <div style={{ textAlign: 'center' }}>
          <div style={{ fontSize: '48px', marginBottom: '20px' }}>‚è≥</div>
          <div>Loading analytics...</div>
          <div style={{ fontSize: '14px', marginTop: '10px', color: '#999' }}>
            Fetching data from server...
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div style={{ 
        padding: '20px', 
        textAlign: 'center',
        backgroundColor: '#fff3cd',
        border: '1px solid #ffeaa7',
        borderRadius: '8px',
        margin: '20px'
      }}>
        <div style={{ fontSize: '48px', marginBottom: '20px' }}>‚ö†Ô∏è</div>
        <h3 style={{ color: '#856404', marginBottom: '15px' }}>Analytics Error</h3>
        <p style={{ color: '#856404', marginBottom: '20px' }}>
          <strong>Error:</strong> {error}
        </p>
        <div>
          <button 
            onClick={handleRetry}
            style={{
              padding: '12px 20px',
              marginRight: '10px',
              backgroundColor: '#007bff',
              color: 'white',
              border: 'none',
              borderRadius: '6px',
              cursor: 'pointer',
              fontSize: '14px'
            }}
          >
            üîÑ Retry
          </button>
          <button 
            onClick={handleBackNavigation}
            style={{
              padding: '12px 20px',
              backgroundColor: '#6c757d',
              color: 'white',
              border: 'none',
              borderRadius: '6px',
              cursor: 'pointer',
              fontSize: '14px'
            }}
          >
            ‚Üê Back
          </button>
        </div>
      </div>
    );
  }

  return (
    <div style={{ 
      padding: '20px', 
      fontFamily: 'Arial, sans-serif',
      backgroundColor: '#f5f5f5',
      minHeight: '100vh'
    }}>
      <div style={{
        backgroundColor: 'white',
        padding: '30px',
        borderRadius: '12px',
        boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
        maxWidth: '1200px',
        margin: '0 auto'
      }}>
        {/* Header */}
        <header style={{ 
          borderBottom: '3px solid #007bff', 
          paddingBottom: '20px', 
          marginBottom: '40px',
          textAlign: 'center'
        }}>
          <h1 style={{ 
            margin: 0, 
            color: '#343a40',
            fontSize: '32px',
            fontWeight: 'bold'
          }}>
            üìä Analytics Dashboard
          </h1>
          <p style={{ 
            margin: '10px 0 0 0', 
            color: '#6c757d',
            fontSize: '16px'
          }}>
            Comprehensive ticket analytics and performance insights
          </p>
        </header>

        {/* Tickets per Week Chart */}
        <section style={{ marginBottom: '50px' }}>
          <CustomBarChart 
            data={analytics.tickets_per_week || []} 
            title="üìà Tickets Created Per Week"
          />
        </section>

        {/* Priority Distribution Chart */}
        <section style={{ marginBottom: '50px' }}>
          <CustomPieChart 
            data={analytics.priority_distribution || {}} 
            title="üéØ Priority Distribution"
          />
        </section>

        {/* Average Resolution Time */}
        <section style={{ marginBottom: '40px' }}>
          <div style={{ padding: '20px', backgroundColor: '#f8f9fa', borderRadius: '8px' }}>
            <h3 style={{ 
              color: '#495057', 
              fontSize: '22px',
              marginBottom: '20px',
              textAlign: 'center'
            }}>
              ‚è±Ô∏è Average Resolution Time
            </h3>
            <div style={{ 
              padding: '40px', 
              border: '3px solid #28a745', 
              borderRadius: '12px',
              backgroundColor: '#d4edda',
              textAlign: 'center',
              maxWidth: '300px',
              margin: '0 auto'
            }}>
              <div style={{ 
                fontSize: '48px', 
                fontWeight: 'bold', 
                color: '#155724',
                marginBottom: '10px'
              }}>
                {analytics.avg_resolution_time || 0}
              </div>
              <div style={{ 
                fontSize: '18px', 
                color: '#155724',
                fontWeight: 'bold'
              }}>
                days
              </div>
              <div style={{ 
                fontSize: '14px', 
                color: '#155724', 
                marginTop: '10px',
                fontStyle: 'italic'
              }}>
                Average time to resolve tickets
              </div>
            </div>
          </div>
        </section>

        {/* Navigation */}
        <div style={{ 
          borderTop: '1px solid #dee2e6', 
          paddingTop: '30px',
          textAlign: 'center'
        }}>
          <button 
            onClick={handleBackNavigation}
            style={{
              padding: '15px 30px',
              backgroundColor: '#007bff',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              fontSize: '16px',
              cursor: 'pointer',
              fontWeight: 'bold',
              transition: 'all 0.3s ease',
              boxShadow: '0 2px 4px rgba(0,0,0,0.2)'
            }}
            onMouseEnter={(e) => {
              e.target.style.backgroundColor = '#0056b3';
              e.target.style.transform = 'translateY(-2px)';
              e.target.style.boxShadow = '0 4px 8px rgba(0,0,0,0.3)';
            }}
            onMouseLeave={(e) => {
              e.target.style.backgroundColor = '#007bff';
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 2px 4px rgba(0,0,0,0.2)';
            }}
          >
            ‚Üê Back to Main Dashboard
          </button>
        </div>
      </div>
    </div>
  );
}
