    const maxValue = Math.max(...data.map(item => item.count));
    const recentData = data.slice(-8);

    return (
      <div style={{
        background: 'linear-gradient(145deg, #ffffff, #f3f4f6)',
        borderRadius: '12px',
        padding: '20px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.05)',
