          {recentData.map((item, index) => {
            const percentage = maxValue > 0 ? (item.count / maxValue) * 100 : 0;
            const isHighest = item.count === maxValue;

            return (
              <div key={index} style={{
                padding: '16px',
                marginBottom: '12px',
