                <div style={{ width: '100%', height: '6px', backgroundColor: '#e5e7eb', borderRadius: '3px', overflow: 'hidden' }}>
                  <div style={{ width: `${percentage}%`, height: '100%', background: isHighest ? '#60a5fa' : '#93c5fd', borderRadius: '3px', transition: 'width 0.6s ease-out' }} />
                </div>
              </div>
            );
          })}
        </div>
      </div>
    );
  };

  const InsightsPanel = () => {
    const insights = [
      {
        icon: TrendingUp,
        title: "Workload Trend",
        description: `${metrics.trend === 'up' ? 'Increasing' : metrics.trend === 'down' ? 'Decreasing' : 'Stable'} ticket volume over recent weeks`,
        color: metrics.trend === 'up' ? '#F59E0B' : metrics.trend === 'down' ? '#10B981' : '#6B7280',
        status: metrics.trend === 'up' ? 'warning' : metrics.trend === 'down' ? 'success' : 'neutral'
      },
      {
        icon: AlertCircle,
        title: "Priority Focus",
        description: `${metrics.highPriorityCount} high-priority tickets require immediate attention`,
        color: metrics.highPriorityCount > 0 ? '#EF4444' : '#10B981',
        status: metrics.highPriorityCount > 0 ? 'critical' : 'success'
      },
      {
        icon: Clock,
        title: "Resolution Efficiency",
        description: `Average ${metrics.avgResolutionTime.toFixed(1)} days resolution time`,
        color: metrics.avgResolutionTime <= 3 ? '#10B981' : metrics.avgResolutionTime <= 7 ? '#F59E0B' : '#EF4444',
