                      <span style={{
                        padding: '2px 6px',
                        fontSize: '10px',
                        fontWeight: '600',
                        borderRadius: '8px',
                        backgroundColor: 
                          insight.status === 'critical' ? '#fee2e2' :
                          insight.status === 'warning' ? '#fef3c7' :
                          insight.status === 'success' ? '#dcfce7' :
                          insight.status === 'excellent' ? '#d1fae5' :
                          '#f3f4f6',
                        color:
                          insight.status === 'critical' ? '#dc2626' :
                          insight.status === 'warning' ? '#d97706' :
                          insight.status === 'success' ? '#16a34a' :
                          insight.status === 'excellent' ? '#059669' :
