  };

  if (loading) {
    return (
      <div style={{ 
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #1e40af 0%, #9333ea 100%)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
