import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { Search, Plus, Edit, Trash2, RefreshCw, Filter, AlertCircle, CheckCircle, Clock, User, Calendar, Tag, Settings, X, Eye, ArrowRight } from 'lucide-react';

// Configuration de l'API
const API_BASE_URL = 'http://localhost:5000/api';

// Service API pour communiquer avec le backend Flask
const apiService = {
  async makeRequest(endpoint, options = {}) {
    const url = `${API_BASE_URL}${endpoint}`;
    const config = {
      headers: {
        'Content-Type': 'application/json',
        ...options.headers,
      },
      ...options,
    };

    try {
      const response = await fetch(url, config);
      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || data.error || `HTTP error! status: ${response.status}`);
      }

      return data;
    } catch (error) {
      console.error('API Request failed:', error);
      throw error;
    }
  },

  async getTickets(filters = {}) {
    const params = new URLSearchParams();
    if (filters.search) params.append('search', filters.search);
    if (filters.assignee && filters.assignee !== 'all') {
      params.append('assignee', filters.assignee);
    }

    const queryString = params.toString();
    const endpoint = queryString ? `/tickets?${queryString}` : '/tickets';

    return await this.makeRequest(endpoint);
  },

  async createTicket(data) {
    return await this.makeRequest('/tickets', {
      method: 'POST',
      body: JSON.stringify({
        summary: data.summary,
        description: data.description,
        issue_type: data.issueType,
        priority: data.priority
      }),
    });
  },

  async updateTicket(key, data) {
    const payload = {};
    if (data.summary && data.summary.trim()) {
      payload.summary = data.summary.trim();
    }
    if (data.description && data.description.trim()) {
      payload.description = data.description.trim();
    }

    return await this.makeRequest(`/tickets/${key}`, {
      method: 'PUT',
      body: JSON.stringify(payload),
    });
  },

  async deleteTicket(key) {
    return await this.makeRequest(`/tickets/${key}`, {
      method: 'DELETE',
    });
  },

  async getTransitions(key) {
    return await this.makeRequest(`/tickets/${key}/transitions`);
  },

  async transitionTicket(key, transitionName) {
    return await this.makeRequest(`/tickets/${key}/transition`, {
      method: 'POST',
      body: JSON.stringify({
        transition_name: transitionName
      }),
    });
  },

  async getStats() {
    return await this.makeRequest('/stats');
  },

  async healthCheck() {
    return await this.makeRequest('/health');
  }
};

// Components
const LoadingSpinner = ({ size = 'md' }) => {
  const sizeClass = {
    sm: 'w-4 h-4',
    md: 'w-6 h-6',
    lg: 'w-8 h-8'
  }[size];

  return (
    <div className={`${sizeClass} animate-spin rounded-full border-2 border-gray-300 border-t-blue-600`}></div>
  );
};

const StatusBadge = ({ status }) => {
  const getStatusConfig = (status) => {
    switch (status.toUpperCase()) {
      case 'TODO':
      case 'TO DO':
      case 'OPEN':
        return { color: 'bg-gray-100 text-gray-800', icon: Clock };
      case 'IN PROGRESS':
      case 'IN-PROGRESS':
      case 'INPROGRESS':
        return { color: 'bg-blue-100 text-blue-800', icon: RefreshCw };
      case 'DONE':
      case 'CLOSED':
      case 'RESOLVED':
        return { color: 'bg-green-100 text-green-800', icon: CheckCircle };
      default:
        return { color: 'bg-purple-100 text-purple-800', icon: Tag };
    }
  };

  const config = getStatusConfig(status);
  const Icon = config.icon;

  return (
    <span className={`inline-flex items-center gap-1 px-2 py-1 rounded-full text-xs font-medium ${config.color}`}>
      <Icon size={12} />
      {status}
    </span>
  );
};

const TicketCard = ({ ticket, onEdit, onDelete, onTransition, onView }) => {
  const [showActions, setShowActions] = useState(false);
  const key = ticket.split(':')[0].trim();
  const rest = ticket.slice(key.length + 1).replace(/^:\s*/, '');
  const summary = rest.split(' [')[0];
  const assignee = rest.match(/\[(.*?)\]/)?.[1] || 'Unassigned';

  return (
    <div 
      className="group bg-white border border-gray-200 rounded-lg p-3 hover:shadow-md transition-all duration-200 relative"
      onMouseEnter={() => setShowActions(true)}
      onMouseLeave={() => setShowActions(false)}
    >
      <div className="flex items-start justify-between gap-2">
        <div className="flex-1 min-w-0">
          <div className="flex items-center gap-2 mb-1">
            <span className="font-mono text-sm font-semibold text-blue-600">{key}</span>
            <span className="text-xs text-gray-500">•</span>
            <span className="text-xs text-gray-500 flex items-center gap-1">
              <User size={10} />
              {assignee}
            </span>
          </div>
          <p className="text-sm text-gray-900 line-clamp-2 mb-2">{summary}</p>
        </div>

        {showActions && (
          <div className="flex items-center gap-1 opacity-0 group-hover:opacity-100 transition-opacity">
            <button
              onClick={() => onView(key)}
              className="p-1 text-gray-400 hover:text-blue-600 rounded"
              title="Voir détails"
            >
              <Eye size={14} />
            </button>
            <button
              onClick={() => onEdit(key)}
              className="p-1 text-gray-400 hover:text-blue-600 rounded"
              title="Modifier"
            >
              <Edit size={14} />
            </button>
            <button
              onClick={() => onTransition(key)}
              className="p-1 text-gray-400 hover:text-green-600 rounded"
              title="Changer statut"
            >
              <ArrowRight size={14} />
            </button>
            <button
              onClick={() => onDelete(key)}
              className="p-1 text-gray-400 hover:text-red-600 rounded"
              title="Supprimer"
            >
              <Trash2 size={14} />
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

const Modal = ({ isOpen, onClose, title, children }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl shadow-xl w-full max-w-md max-h-[90vh] overflow-hidden">
        <div className="flex items-center justify-between p-4 border-b">
          <h2 className="text-lg font-semibold">{title}</h2>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
            <X size={20} />
          </button>
        </div>
        <div className="p-4 max-h-[calc(90vh-80px)] overflow-y-auto">
          {children}
        </div>
      </div>
    </div>
  );
};

const CreateTicketModal = ({ isOpen, onClose, onSubmit, loading }) => {
  const [formData, setFormData] = useState({
    summary: '',
    description: '',
    issueType: 'Task',
    priority: 'Medium'
  });
  const [errors, setErrors] = useState({});

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = {};

    if (!formData.summary.trim()) {
      newErrors.summary = 'Le résumé est requis';
    }

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    const success = await onSubmit(formData);
    if (success) {
      setFormData({ summary: '', description: '', issueType: 'Task', priority: 'Medium' });
      setErrors({});
      onClose();
    }
  };

  const handleChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  return (
    <Modal isOpen={isOpen} onClose={onClose} title="Créer un nouveau ticket">
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Résumé *
          </label>
          <input
            type="text"
            value={formData.summary}
            onChange={(e) => handleChange('summary', e.target.value)}
            className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent ${
              errors.summary ? 'border-red-300' : 'border-gray-300'
            }`}
            placeholder="Titre du ticket"
          />
          {errors.summary && (
            <p className="text-red-600 text-sm mt-1">{errors.summary}</p>
          )}
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Description
          </label>
          <textarea
            value={formData.description}
            onChange={(e) => handleChange('description', e.target.value)}
            rows={4}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            placeholder="Description détaillée du ticket"
          />
        </div>

        <div className="grid grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Type
            </label>
            <select
              value={formData.issueType}
              onChange={(e) => handleChange('issueType', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="Task">Task</option>
              <option value="Bug">Bug</option>
              <option value="Story">Story</option>
              <option value="Epic">Epic</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Priorité
            </label>
            <select
              value={formData.priority}
              onChange={(e) => handleChange('priority', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="Low">Low</option>
              <option value="Medium">Medium</option>
              <option value="High">High</option>
              <option value="Critical">Critical</option>
            </select>
          </div>
        </div>

        <div className="flex justify-end gap-3 pt-4 border-t">
          <button
            type="button"
            onClick={onClose}
            className="px-4 py-2 text-gray-700 border border-gray-300 rounded-lg hover:bg-gray-50"
            disabled={loading}
          >
            Annuler
          </button>
          <button
            type="submit"
            disabled={loading}
            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 flex items-center gap-2"
          >
            {loading && <LoadingSpinner size="sm" />}
            {loading ? 'Création...' : 'Créer le ticket'}
          </button>
        </div>
      </form>
    </Modal>
  );
};

const EditTicketModal = ({ isOpen, onClose, onSubmit, loading, ticketKey }) => {
  const [formData, setFormData] = useState({
    summary: '',
    description: ''
  });
  const [errors, setErrors] = useState({});

  // Reset form when modal opens/closes or ticket changes
  useEffect(() => {
    if (!isOpen) {
      setFormData({ summary: '', description: '' });
      setErrors({});
    }
  }, [isOpen, ticketKey]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.summary.trim() && !formData.description.trim()) {
      setErrors({ general: 'Au moins un champ doit être modifié' });
      return;
    }

    const success = await onSubmit(ticketKey, formData);
    if (success) {
      setFormData({ summary: '', description: '' });
      setErrors({});
      onClose();
    }
  };

  return (
    <Modal isOpen={isOpen} onClose={onClose} title={`Modifier ${ticketKey || 'Ticket'}`}>
      <form onSubmit={handleSubmit} className="space-y-4">
        {errors.general && (
          <div className="bg-red-50 border border-red-200 rounded-lg p-3">
            <p className="text-red-600 text-sm">{errors.general}</p>
          </div>
        )}

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Nouveau résumé
          </label>
          <input
            type="text"
            value={formData.summary}
            onChange={(e) => setFormData(prev => ({ ...prev, summary: e.target.value }))}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            placeholder="Laisser vide pour ne pas changer"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Nouvelle description
          </label>
          <textarea
            value={formData.description}
            onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
            rows={4}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            placeholder="Laisser vide pour ne pas changer"
          />
        </div>

        <div className="flex justify-end gap-3 pt-4 border-t">
          <button
            type="button"
            onClick={onClose}
            className="px-4 py-2 text-gray-700 border border-gray-300 rounded-lg hover:bg-gray-50"
            disabled={loading}
          >
            Annuler
          </button>
          <button
            type="submit"
            disabled={loading}
            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 flex items-center gap-2"
          >
            {loading && <LoadingSpinner size="sm" />}
            {loading ? 'Mise à jour...' : 'Mettre à jour'}
          </button>
        </div>
      </form>
    </Modal>
  );
};

const TransitionModal = ({ isOpen, onClose, onSubmit, loading, ticketKey, currentStatus }) => {
  const [selectedTransition, setSelectedTransition] = useState('');
  const [availableTransitions, setAvailableTransitions] = useState([]);
  const [loadingTransitions, setLoadingTransitions] = useState(false);

  // Reset form when modal opens/closes or ticket changes
  useEffect(() => {
    if (!isOpen) {
      setSelectedTransition('');
      setAvailableTransitions([]);
    }
  }, [isOpen]);

  useEffect(() => {
    if (isOpen && ticketKey) {
      loadTransitions();
    }
  }, [isOpen, ticketKey]);

  const loadTransitions = async () => {
    setLoadingTransitions(true);
    try {
      const response = await apiService.getTransitions(ticketKey);
      if (response.success && response.transitions) {
        setAvailableTransitions(Object.keys(response.transitions));
      } else {
        setAvailableTransitions([]);
      }
    } catch (error) {
      console.error('Error loading transitions:', error);
      setAvailableTransitions([]);
    } finally {
      setLoadingTransitions(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (selectedTransition) {
      const success = await onSubmit(ticketKey, selectedTransition);
      if (success) {
        setSelectedTransition('');
        onClose();
      }
    }
  };

  return (
    <Modal isOpen={isOpen} onClose={onClose} title={`Changer le statut de ${ticketKey || 'Ticket'}`}>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-3">
            Statut actuel: {currentStatus ? <StatusBadge status={currentStatus} /> : <span className="text-gray-400">Chargement...</span>}
          </label>

          {loadingTransitions ? (
            <div className="flex items-center justify-center py-4">
              <LoadingSpinner size="sm" />
              <span className="ml-2 text-sm text-gray-600">Chargement des transitions...</span>
            </div>
          ) : (
            <>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Nouvelle transition
              </label>
              <div className="space-y-2">
                {availableTransitions.length === 0 ? (
                  <p className="text-sm text-gray-500">Aucune transition disponible</p>
                ) : (
                  availableTransitions.map(transition => (
                    <label key={transition} className="flex items-center gap-3 p-3 border rounded-lg hover:bg-gray-50 cursor-pointer">
                      <input
                        type="radio"
                        name="transition"
                        value={transition}
                        checked={selectedTransition === transition}
                        onChange={(e) => setSelectedTransition(e.target.value)}
                        className="text-blue-600 focus:ring-blue-500"
                      />
                      <span className="text-sm font-medium">{transition}</span>
                    </label>
                  ))
                )}
              </div>
            </>
          )}
        </div>

        <div className="flex justify-end gap-3 pt-4 border-t">
          <button
            type="button"
            onClick={onClose}
            className="px-4 py-2 text-gray-700 border border-gray-300 rounded-lg hover:bg-gray-50"
            disabled={loading}
          >
            Annuler
          </button>
          <button
            type="submit"
            disabled={loading || !selectedTransition || loadingTransitions}
            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 flex items-center gap-2"
          >
            {loading && <LoadingSpinner size="sm" />}
            {loading ? 'Changement...' : 'Changer le statut'}
          </button>
        </div>
      </form>
    </Modal>
  );
};

const ConfirmModal = ({ isOpen, onClose, onConfirm, title, message, loading }) => (
  <Modal isOpen={isOpen} onClose={onClose} title={title}>
    <div className="space-y-4">
      <div className="flex items-start gap-3">
        <AlertCircle className="text-red-500 mt-0.5" size={20} />
        <p className="text-gray-700">{message}</p>
      </div>

      <div className="flex justify-end gap-3 pt-4 border-t">
        <button
          onClick={onClose}
          className="px-4 py-2 text-gray-700 border border-gray-300 rounded-lg hover:bg-gray-50"
          disabled={loading}
        >
          Annuler
        </button>
        <button
          onClick={onConfirm}
          disabled={loading}
          className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 disabled:opacity-50 flex items-center gap-2"
        >
          {loading && <LoadingSpinner size="sm" />}
          {loading ? 'Suppression...' : 'Supprimer'}
        </button>
      </div>
    </div>
  </Modal>
);

// Main Component
export default function JiraManagerPro() {
  const [tickets, setTickets] = useState({});
  const [loading, setLoading] = useState(true);
  const [actionLoading, setActionLoading] = useState(false);
  const [error, setError] = useState('');
  const [connectionStatus, setConnectionStatus] = useState('unknown');
  const [filters, setFilters] = useState({
    search: '',
    assignee: 'all'
  });

  // Modal states
  const [modals, setModals] = useState({
    create: false,
    edit: false,
    transition: false,
    confirm: false
  });

  const [selectedTicket, setSelectedTicket] = useState(null);
  const [confirmAction, setConfirmAction] = useState(null);

  const openModal = useCallback((type, ticketKey = null) => {
    console.log('Opening modal:', type, 'for ticket:', ticketKey); // Debug log
    setSelectedTicket(ticketKey);
    setModals(prev => ({ ...prev, [type]: true }));
  }, []);

  const closeModal = useCallback((type) => {
    setModals(prev => ({ ...prev, [type]: false }));
    setSelectedTicket(null);
    setConfirmAction(null);
  }, []);

  // Check API health
  const checkHealth = useCallback(async () => {
    try {
      const response = await apiService.healthCheck();
      setConnectionStatus(response.jira_connection === 'connected' ? 'connected' : 'disconnected');
    } catch (error) {
      setConnectionStatus('error');
    }
  }, []);

  const loadTickets = useCallback(async (currentFilters = filters) => {
    try {
      setLoading(true);
      setError('');
      const data = await apiService.getTickets(currentFilters);
      setTickets(data);
    } catch (err) {
      setError(err.message || 'Impossible de charger les tickets');
      setTickets({});
    } finally {
      setLoading(false);
    }
  }, [filters]);

  useEffect(() => {
    checkHealth();
    loadTickets();
  }, [checkHealth, loadTickets]);

  const handleCreateTicket = async (formData) => {
    setActionLoading(true);
    try {
      const result = await apiService.createTicket(formData);
      if (result.success) {
        await loadTickets();
        return true;
      }
    } catch (err) {
      setError(err.message || 'Erreur lors de la création du ticket');
    } finally {
      setActionLoading(false);
    }
    return false;
  };

  const handleEditTicket = async (ticketKey, formData) => {
    setActionLoading(true);
    try {
      const result = await apiService.updateTicket(ticketKey, formData);
      if (result.success) {
        await loadTickets();
        return true;
      }
    } catch (err) {
      setError(err.message || 'Erreur lors de la modification du ticket');
    } finally {
      setActionLoading(false);
    }
    return false;
  };

  const handleTransitionTicket = async (ticketKey, transitionName) => {
    setActionLoading(true);
    try {
      const result = await apiService.transitionTicket(ticketKey, transitionName);
      if (result.success) {
        await loadTickets();
        return true;
      }
    } catch (err) {
      setError(err.message || 'Erreur lors du changement de statut');
    } finally {
      setActionLoading(false);
    }
    return false;
  };

  const handleDeleteTicket = async () => {
    if (!confirmAction?.ticketKey) return;

    setActionLoading(true);
    try {
      const result = await apiService.deleteTicket(confirmAction.ticketKey);
      if (result.success) {
        await loadTickets();
        closeModal('confirm');
      }
    } catch (err) {
      setError(err.message || 'Erreur lors de la suppression du ticket');
    } finally {
      setActionLoading(false);
    }
  };

  const handleFilterChange = (newFilters) => {
    setFilters(newFilters);
    loadTickets(newFilters);
  };

  const findTicketStatus = (ticketKey) => {
    for (const [status, ticketList] of Object.entries(tickets)) {
      if (ticketList.some(ticket => ticket.startsWith(ticketKey))) {
        return status;
      }
    }
    return null;
  };

  const statuses = Object.keys(tickets);
  const totalTickets = Object.values(tickets).flat().length;
  const uniqueAssignees = useMemo(() => {
    const assignees = new Set();
    Object.values(tickets).flat().forEach(ticket => {
      const assignee = ticket.match(/\[(.*?)\]/)?.[1];
      if (assignee) assignees.add(assignee);
    });
    return Array.from(assignees).filter(a => a !== 'Unassigned');
  }, [tickets]);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white border-b border-gray-200 sticky top-0 z-40">
        <div className="max-w-7xl mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <h1 className="text-2xl font-bold text-gray-900">Jira Manager Pro</h1>
              <div className="flex items-center gap-2 text-sm text-gray-500">
                <span>{totalTickets} tickets</span>
                <span>•</span>
                <span>{statuses.length} statuts</span>
                <span>•</span>
                <div className="flex items-center gap-1">
                  <div className={`w-2 h-2 rounded-full ${
                    connectionStatus === 'connected' ? 'bg-green-500' : 
                    connectionStatus === 'disconnected' ? 'bg-orange-500' : 'bg-red-500'
                  }`}></div>
                  <span className="text-xs">
                    {connectionStatus === 'connected' ? 'Connecté' : 
                     connectionStatus === 'disconnected' ? 'Déconnecté' : 'Erreur'}
                  </span>
                </div>
              </div>
            </div>

            <div className="flex items-center gap-3">
              <button
                onClick={() => { checkHealth(); loadTickets(); }}
                disabled={loading}
                className="flex items-center gap-2 px-3 py-2 text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded-lg transition-colors"
                title="Actualiser"
              >
                <RefreshCw size={18} className={loading ? 'animate-spin' : ''} />
              </button>

              <button
                onClick={() => openModal('create')}
                className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                disabled={connectionStatus !== 'connected'}
              >
                <Plus size={18} />
                Nouveau ticket
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Connection Warning */}
      {connectionStatus !== 'connected' && (
        <div className="max-w-7xl mx-auto px-4 py-2">
          <div className="bg-orange-50 border border-orange-200 rounded-lg p-4 flex items-center gap-3">
            <AlertCircle className="text-orange-500" size={20} />
            <div>
              <p className="text-orange-700 font-medium">
                {connectionStatus === 'disconnected' ? 'Connexion Jira déconnectée' : 'Erreur de connexion'}
              </p>
              <p className="text-orange-600 text-sm">
                Vérifiez que l'API est démarrée et que la configuration Jira est correcte.
              </p>
            </div>
          </div>
        </div>
      )}

      {/* Filters */}
      <div className="max-w-7xl mx-auto px-4 py-4">
        <div className="bg-white rounded-xl border border-gray-200 p-4 mb-6">
          <div className="flex flex-wrap items-center gap-4">
            <div className="flex items-center gap-2">
              <Search size={18} className="text-gray-400" />
              <input
                type="text"
                placeholder="Rechercher des tickets..."
                value={filters.search}
                onChange={(e) => handleFilterChange({ ...filters, search: e.target.value })}
                className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>

            <div className="flex items-center gap-2">
              <Filter size={18} className="text-gray-400" />
              <select
                value={filters.assignee}
                onChange={(e) => handleFilterChange({ ...filters, assignee: e.target.value })}
                className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              >
                <option value="all">Tous les assignés</option>
                <option value="unassigned">Non assignés</option>
                {uniqueAssignees.map(assignee => (
                  <option key={assignee} value={assignee}>{assignee}</option>
                ))}
              </select>
            </div>
          </div>
        </div>

        {/* Error Message */}
        {error && (
          <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-6 flex items-center gap-3">
            <AlertCircle className="text-red-500" size={20} />
            <span className="text-red-700">{error}</span>
            <button
              onClick={() => setError('')}
              className="ml-auto text-red-500 hover:text-red-700"
            >
              <X size={18} />
            </button>
          </div>
        )}

        {/* Loading State */}
        {loading ? (
          <div className="flex items-center justify-center py-12">
            <LoadingSpinner size="lg" />
            <span className="ml-3 text-gray-600">Chargement des tickets...</span>
          </div>
        ) : (
          /* Tickets Grid */
          <div className="grid lg:grid-cols-3 gap-6">
            {statuses.length === 0 ? (
              <div className="col-span-3 text-center py-12">
                <div className="text-gray-400 mb-4">
                  <Tag size={48} className="mx-auto" />
                </div>
                <h3 className="text-lg font-medium text-gray-900 mb-2">Aucun ticket trouvé</h3>
                <p className="text-gray-500 mb-4">
                  {connectionStatus === 'connected' 
                    ? 'Créez votre premier ticket pour commencer' 
                    : 'Vérifiez la connexion à Jira'}
                </p>
                {connectionStatus === 'connected' && (
                  <button
                    onClick={() => openModal('create')}
                    className="inline-flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                  >
                    <Plus size={18} />
                    Créer un ticket
                  </button>
                )}
              </div>
            ) : (
              statuses.map(status => (
                <div key={status} className="bg-white rounded-xl border border-gray-200 overflow-hidden">
                  <div className="p-4 border-b border-gray-100 bg-gray-50">
                    <div className="flex items-center justify-between">
                      <StatusBadge status={status} />
                      <span className="text-sm text-gray-500 font-medium">
                        {tickets[status]?.length || 0}
                      </span>
                    </div>
                  </div>

                  <div className="p-4 space-y-3 max-h-[600px] overflow-y-auto">
                    {tickets[status]?.length === 0 ? (
                      <div className="text-center py-8 text-gray-500">
                        <Clock size={24} className="mx-auto mb-2 opacity-50" />
                        <p className="text-sm">Aucun ticket</p>
                      </div>
                    ) : (
                      tickets[status]?.map((ticket, index) => {
                        const ticketKey = ticket.split(':')[0].trim();
                        return (
                          <TicketCard
                            key={`${ticketKey}-${index}`}
                            ticket={ticket}
                            onEdit={(key) => openModal('edit', key)}
                            onDelete={(key) => {
                              setConfirmAction({
                                ticketKey: key,
                                title: 'Supprimer le ticket',
                                message: `Êtes-vous sûr de vouloir supprimer le ticket ${key} ? Cette action est irréversible.`
                              });
                              openModal('confirm');
                            }}
                            onTransition={(key) => openModal('transition', key)}
                            onView={(key) => {
                              console.log('View ticket:', key);
                            }}
                          />
                        );
                      })
                    )}
                  </div>
                </div>
              ))
            )}
          </div>
        )}
      </div>

      {/* Modals */}
      <CreateTicketModal
        isOpen={modals.create}
        onClose={() => closeModal('create')}
        onSubmit={handleCreateTicket}
        loading={actionLoading}
      />

      <EditTicketModal
        isOpen={modals.edit}
        onClose={() => closeModal('edit')}
        onSubmit={handleEditTicket}
        loading={actionLoading}
        ticketKey={selectedTicket}
      />

      <TransitionModal
        isOpen={modals.transition}
        onClose={() => closeModal('transition')}
        onSubmit={handleTransitionTicket}
        loading={actionLoading}
        ticketKey={selectedTicket}
        currentStatus={selectedTicket ? findTicketStatus(selectedTicket) : null}
      />

      <ConfirmModal
        isOpen={modals.confirm}
        onClose={() => closeModal('confirm')}
        onConfirm={handleDeleteTicket}
        loading={actionLoading}
        title={confirmAction?.title || ''}
        message={confirmAction?.message || ''}
      />

      {/* Footer */}
      <footer className="mt-12 py-8 border-t border-gray-200 bg-white">
        <div className="max-w-7xl mx-auto px-4 text-center text-gray-500">
          <p className="text-sm">Jira Manager Pro - Interface moderne pour la gestion de tickets</p>
          <p className="text-xs mt-1">
            API Status: 
            <span className={`ml-1 font-medium ${
              connectionStatus === 'connected' ? 'text-green-600' : 
              connectionStatus === 'disconnected' ? 'text-orange-600' : 'text-red-600'
            }`}>
              {connectionStatus === 'connected' ? 'Connecté à Jira' : 
               connectionStatus === 'disconnected' ? 'Jira déconnecté' : 'Erreur de connexion'}
            </span>
          </p>
        </div>
      </footer>
    </div>
  );
}
