// Components
const LoadingSpinner = ({ size = 'md' }) => {
  return (
    <div className={`jira-spinner jira-spinner-${size}`}></div>
  );
};

const StatusBadge = ({ status }) => {
  const getStatusClass = (status) => {
    const normalizedStatus = status.toUpperCase().replace(/[^A-Z]/g, '');

    switch (normalizedStatus) {
      case 'TODO':
      case 'TODO':
      case 'AFAIRE':
      case 'OPEN':
        return 'jira-status-todo';
      case 'INPROGRESS':
      case 'ENCOURS':
      case 'PROGRESS':
        return 'jira-status-progress';
      case 'DONE':
      case 'TERMINE':
      case 'TERMINEES':
      case 'CLOSED':
      case 'RESOLVED':
        return 'jira-status-done';
      default:
        return 'jira-status-other';
    }
  };

  const getStatusIcon = (status) => {
    const normalizedStatus = status.toUpperCase().replace(/[^A-Z]/g, '');

    switch (normalizedStatus) {
      case 'TODO':
      case 'AFAIRE':
      case 'OPEN':
        return Clock;
      case 'INPROGRESS':
      case 'ENCOURS':
      case 'PROGRESS':
        return RefreshCw;
      case 'DONE':
      case 'TERMINE':
      case 'TERMINEES':
      case 'CLOSED':
      case 'RESOLVED':
        return CheckCircle;
      default:
        return Tag;
    }
  };

  const statusClass = getStatusClass(status);
  const Icon = getStatusIcon(status);

  return (
    <span className={`jira-status-badge ${statusClass}`}>
      <Icon size={12} />
      {status}
    </span>
  );
};

const TicketCard = ({ ticket, onEdit, onDelete, onTransition, onView }) => {
  const key = ticket.split(':')[0].trim();
  const rest = ticket.slice(key.length + 1).replace(/^:\s*/, '');
  const summary = rest.split(' [')[0];
  const assignee = rest.match(/\[(.*?)\]/)?.[1] || 'Unassigned';

  return (
    <div className="jira-ticket-card">
      <div className="jira-ticket-header">
        <div className="jira-flex-1 jira-min-w-0">
          <div className="jira-ticket-meta">
            <span className="jira-ticket-key">{key}</span>
            <span>•</span>
            <div className="jira-ticket-assignee">
              <User size={10} />
              {assignee}
            </div>
          </div>
          <p className="jira-ticket-summary">{summary}</p>
        </div>

        <div className="jira-ticket-actions">
          <button
            onClick={() => onView(key)}
            className="jira-ticket-action"
            title="Voir détails"
          >
            <Eye size={14} />
          </button>
          <button
            onClick={() => onEdit(key)}
            className="jira-ticket-action"
            title="Modifier"
          >
            <Edit size={14} />
          </button>
          <button
            onClick={() => onTransition(key)}
            className="jira-ticket-action success"
            title="Changer statut"
          >
            <ArrowRight size={14} />
          </button>
          <button
            onClick={() => onDelete(key)}
            className="jira-ticket-action danger"
            title="Supprimer"
          >
            <Trash2 size={14} />
          </button>
        </div>
      </div>
    </div>
  );
};

const Modal = ({ isOpen, onClose, title, children }) => {
  if (!isOpen) return null;

  return (
    <div className="jira-modal-overlay">
      <div className="jira-modal">
        <div className="jira-modal-header">
          <h2 className="jira-modal-title">{title}</h2>
          <button onClick={onClose} className="jira-modal-close">
            <X size={20} />
          </button>
        </div>
        <div className="jira-modal-body">
          {children}
        </div>
      </div>
    </div>
  );
};

const ViewTicketModal = ({ isOpen, onClose, ticketKey, loading }) => {
  const [ticketDetails, setTicketDetails] = useState(null);
  const [detailsLoading, setDetailsLoading] = useState(false);

  useEffect(() => {
    if (isOpen && ticketKey) {
      loadTicketDetails();
    } else {
      setTicketDetails(null);
    }
  }, [isOpen, ticketKey]);

  const loadTicketDetails = async () => {
    setDetailsLoading(true);
    try {
      const response = await apiService.getTicketDetails(ticketKey);
      if (response.success) {
        setTicketDetails(response.ticket);
      }
    } catch (error) {
      console.error('Error loading ticket details:', error);
      // Fallback: create basic details from ticket key
      setTicketDetails({
        key: ticketKey,
        summary: 'Détails indisponibles',
        description: 'Impossible de charger les détails complets du ticket.',
        status: 'Inconnu',
        assignee: 'Non assigné',
        priority: 'Inconnu',
        created: 'Inconnu',
        updated: 'Inconnu'
      });
    } finally {
      setDetailsLoading(false);
    }
  };

  return (
    <Modal isOpen={isOpen} onClose={onClose} title={`Détails - ${ticketKey || ''}`}>
      {detailsLoading ? (
        <div className="jira-flex jira-items-center jira-justify-center jira-py-12">
          <LoadingSpinner size="sm" />
          <span style={{marginLeft: '0.5rem', fontSize: '0.875rem', color: '#64748b'}}>
            Chargement des détails...
          </span>
        </div>
      ) : ticketDetails ? (
        <div className="ticket-details">
          <div className="ticket-field">
            <div className="ticket-field-label">Clé du ticket</div>
            <div className="ticket-field-value">{ticketDetails.key || ticketKey}</div>
          </div>

          <div className="ticket-field">
            <div className="ticket-field-label">Résumé</div>
            <div className="ticket-field-value">
              {ticketDetails.summary || 'Non spécifié'}
            </div>
          </div>

          <div className="ticket-field">
            <div className="ticket-field-label">Description</div>
            <div className={`ticket-field-value ${!ticketDetails.description ? 'empty' : ''}`}>
              {ticketDetails.description || 'Aucune description'}
            </div>
          </div>

          <div className="ticket-field">
            <div className="ticket-field-label">Statut</div>
            <div className="ticket-field-value">
              {ticketDetails.status ? (
                <StatusBadge status={ticketDetails.status} />
              ) : (
                'Inconnu'
              )}
            </div>
          </div>

          <div className="ticket-field">
            <div className="ticket-field-label">Assigné à</div>
            <div className="ticket-field-value">
              <div className="jira-flex jira-items-center jira-gap-2">
                <User size={14} />
                {ticketDetails.assignee || 'Non assigné'}
