          {ticketDetails.priority && (
            <div className="ticket-field">
              <div className="ticket-field-label">Priorité</div>
              <div className="ticket-field-value">{ticketDetails.priority}</div>
            </div>
          )}

          {ticketDetails.issueType && (
            <div className="ticket-field">
              <div className="ticket-field-label">Type</div>
              <div className="ticket-field-value">{ticketDetails.issueType}</div>
            </div>
          )}

          {ticketDetails.created && (
            <div className="ticket-field">
              <div className="ticket-field-label">Créé le</div>
