const CreateTicketModal = ({ isOpen, onClose, onSubmit, loading }) => {
  const [formData, setFormData] = useState({
    summary: '',
    description: '',
    issueType: 'Task',
    priority: 'Medium'
  });
  const [errors, setErrors] = useState({});

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = {};

    if (!formData.summary.trim()) {
      newErrors.summary = 'Le résumé est requis';
    }

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    const success = await onSubmit(formData);
    if (success) {
      setFormData({ summary: '', description: '', issueType: 'Task', priority: 'Medium' });
      setErrors({});
      onClose();
    }
  };

  const handleChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  return (
    <Modal isOpen={isOpen} onClose={onClose} title="Créer un nouveau ticket">
      <form onSubmit={handleSubmit}>
        <div className="jira-form-group">
          <label className="jira-form-label">
            Résumé *
          </label>
          <input
            type="text"
            value={formData.summary}
            onChange={(e) => handleChange('summary', e.target.value)}
            className={`jira-form-input jira-w-full ${errors.summary ? 'border-red-300' : ''}`}
            placeholder="Titre du ticket"
          />
          {errors.summary && (
            <p className="jira-form-error">{errors.summary}</p>
          )}
        </div>

        <div className="jira-form-group">
          <label className="jira-form-label">
            Description
          </label>
          <textarea
            value={formData.description}
            onChange={(e) => handleChange('description', e.target.value)}
            className="jira-form-textarea jira-w-full"
            placeholder="Description détaillée du ticket"
          />
        </div>

        <div className="jira-form-row">
          <div className="jira-form-group">
            <label className="jira-form-label">
              Type
            </label>
            <select
              value={formData.issueType}
              onChange={(e) => handleChange('issueType', e.target.value)}
              className="jira-form-select jira-w-full"
            >
              <option value="Task">Task</option>
              <option value="Bug">Bug</option>
              <option value="Story">Story</option>
              <option value="Epic">Epic</option>
            </select>
          </div>

          <div className="jira-form-group">
            <label className="jira-form-label">
              Priorité
            </label>
            <select
              value={formData.priority}
              onChange={(e) => handleChange('priority', e.target.value)}
              className="jira-form-select jira-w-full"
            >
              <option value="Low">Low</option>
              <option value="Medium">Medium</option>
              <option value="High">High</option>
              <option value="Critical">Critical</option>
            </select>
          </div>
        </div>

        <div className="jira-form-actions">
          <button
            type="button"
            onClick={onClose}
            className="jira-btn jira-btn-secondary"
            disabled={loading}
          >
            Annuler
          </button>
          <button
            type="submit"
            disabled={loading}
            className="jira-btn jira-btn-primary"
          >
            {loading && <LoadingSpinner size="sm" />}
            {loading ? 'Création...' : 'Créer le ticket'}
          </button>
        </div>
      </form>
    </Modal>
  );
};

const EditTicketModal = ({ isOpen, onClose, onSubmit, loading, ticketKey }) => {
  const [formData, setFormData] = useState({
    summary: '',
    description: ''
  });
  const [errors, setErrors] = useState({});

  // Reset form when modal opens/closes or ticket changes
  useEffect(() => {
    if (!isOpen) {
      setFormData({ summary: '', description: '' });
      setErrors({});
    }
  }, [isOpen, ticketKey]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.summary.trim() && !formData.description.trim()) {
      setErrors({ general: 'Au moins un champ doit être modifié' });
      return;
    }

    const success = await onSubmit(ticketKey, formData);
    if (success) {
      setFormData({ summary: '', description: '' });
      setErrors({});
      onClose();
    }
