import React, { useState, useEffect } from 'react';
import { BarChart3, TrendingUp, Users, Clock, Target, ArrowUp, ArrowDown, Minus, RefreshCw, Home, ChevronRight, Eye, Activity } from 'lucide-react';

export default function AnalyticsDashboard() {
  const [analytics, setAnalytics] = useState({
    tickets_per_week: [],
    priority_distribution: {},
    avg_resolution_time: 0.0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeView, setActiveView] = useState('overview'); // 'overview' or 'trends'

  useEffect(() => {
    const fetchAnalytics = async () => {
      try {
        const url = 'http://localhost:5000/api/analytics';
        console.log('Fetching analytics from:', url);

        const response = await fetch(url, {
          method: 'GET',
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
          },
          mode: 'cors'
        });

        if (!response.ok) {
          throw new Error(`HTTP ${response.status}: ${response.statusText}`);
        }

        const responseText = await response.text();

        if (!responseText || responseText.trim() === '') {
          throw new Error('Empty response from server');
        }

        let parsedData;
        try {
          parsedData = JSON.parse(responseText);
        } catch (parseError) {
          throw new Error(`Invalid JSON response: ${parseError.message}`);
        }

        setAnalytics(parsedData);

      } catch (err) {
        console.error('Analytics fetch error:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    const timeoutId = setTimeout(fetchAnalytics, 100);
    return () => clearTimeout(timeoutId);
  }, []);

  const handleBackNavigation = () => {
    if (window.history.length > 1) {
      window.history.back();
    } else {
      window.location.href = '/';
    }
  };

  const handleRetry = () => {
    setLoading(true);
    setError(null);
    window.location.reload();
  };

  // Calculate enhanced metrics
  const calculateMetrics = () => {
    const weeklyData = analytics.tickets_per_week || [];
    const totalTickets = weeklyData.reduce((sum, week) => sum + week.count, 0);

    // Mock additional metrics (in real app, these would come from backend)
    const resolvedTickets = Math.floor(totalTickets * 0.85);
    const completionRate = totalTickets > 0 ? (resolvedTickets / totalTickets * 100) : 0;

    // Calculate trends
    const recentWeeks = weeklyData.slice(-2);
    let trend = 'stable';
    let trendPercentage = 0;

    if (recentWeeks.length === 2) {
      const [prev, current] = recentWeeks;
      trendPercentage = prev.count > 0 ? ((current.count - prev.count) / prev.count * 100) : 0;
      trend = trendPercentage > 5 ? 'up' : trendPercentage < -5 ? 'down' : 'stable';
    }

    return {
      totalTickets,
      resolvedTickets,
      completionRate,
      trend,
      trendPercentage: Math.abs(trendPercentage),
      avgResolutionTime: analytics.avg_resolution_time || 0,
      activeTickets: totalTickets - resolvedTickets
    };
  };

  const metrics = calculateMetrics();

  // Professional KPI Card Component
  const KPICard = ({ title, value, subtitle, icon: Icon, trend, trendValue, color = "blue" }) => {
    const colorClasses = {
      blue: "bg-blue-50 border-blue-200 text-blue-800",
      green: "bg-green-50 border-green-200 text-green-800",
      orange: "bg-orange-50 border-orange-200 text-orange-800",
      purple: "bg-purple-50 border-purple-200 text-purple-800",
      red: "bg-red-50 border-red-200 text-red-800"
    };

    const TrendIcon = trend === 'up' ? ArrowUp : trend === 'down' ? ArrowDown : Minus;
    const trendColorClass = trend === 'up' ? 'text-green-600' : trend === 'down' ? 'text-red-600' : 'text-gray-600';

    return (
      <div className="bg-white rounded-xl border border-gray-200 p-6 shadow-sm hover:shadow-md transition-all duration-200 hover:border-gray-300">
        <div className="flex items-center justify-between mb-4">
          <div className={`inline-flex p-3 rounded-lg ${colorClasses[color]}`}>
            <Icon className="w-6 h-6" />
          </div>
          {trend && (
            <div className={`flex items-center space-x-1 ${trendColorClass}`}>
              <TrendIcon className="w-4 h-4" />
              <span className="text-sm font-medium">{trendValue.toFixed(1)}%</span>
            </div>
          )}
        </div>
        <div className="space-y-2">
          <h3 className="text-2xl font-bold text-gray-900">{value}</h3>
          <p className="text-sm font-medium text-gray-600">{title}</p>
          {subtitle && <p className="text-xs text-gray-500">{subtitle}</p>}
        </div>
      </div>
    );
  };

  // Professional Donut Chart Component
  const DonutChart = ({ data, title, centerText }) => {
    if (!data || Object.keys(data).length === 0) return <div className="text-gray-500">No data available</div>;

    const entries = Object.entries(data);
    const total = entries.reduce((sum, [_, value]) => sum + value, 0);
    const colors = ['#3B82F6', '#10B981', '#F59E0B', '#EF4444', '#8B5CF6'];

    let cumulativeAngle = 0;
    const radius = 80;
    const innerRadius = 50;
    const centerX = 120;
    const centerY = 120;

    return (
      <div className="bg-white rounded-xl border border-gray-200 p-6 shadow-sm">
        <h3 className="text-lg font-semibold text-gray-900 mb-6">{title}</h3>
        <div className="flex items-center justify-between">
          <div className="relative">
            <svg width="240" height="240" className="overflow-visible">
              {entries.map(([name, value], index) => {
                const percentage = (value / total) * 100;
                const angle = (value / total) * 360;
                const startAngle = cumulativeAngle;
                const endAngle = cumulativeAngle + angle;

                const startRad = (startAngle * Math.PI) / 180;
                const endRad = (endAngle * Math.PI) / 180;

                const x1Outer = centerX + radius * Math.cos(startRad);
                const y1Outer = centerY + radius * Math.sin(startRad);
                const x2Outer = centerX + radius * Math.cos(endRad);
                const y2Outer = centerY + radius * Math.sin(endRad);

                const x1Inner = centerX + innerRadius * Math.cos(startRad);
                const y1Inner = centerY + innerRadius * Math.sin(startRad);
                const x2Inner = centerX + innerRadius * Math.cos(endRad);
                const y2Inner = centerY + innerRadius * Math.sin(endRad);

                const largeArcFlag = angle > 180 ? 1 : 0;

                const pathData = [
                  `M ${x1Inner} ${y1Inner}`,
                  `L ${x1Outer} ${y1Outer}`,
                  `A ${radius} ${radius} 0 ${largeArcFlag} 1 ${x2Outer} ${y2Outer}`,
                  `L ${x2Inner} ${y2Inner}`,
                  `A ${innerRadius} ${innerRadius} 0 ${largeArcFlag} 0 ${x1Inner} ${y1Inner}`,
                  'Z'
                ].join(' ');

                cumulativeAngle += angle;

                return (
                  <path
                    key={index}
                    d={pathData}
                    fill={colors[index % colors.length]}
                    stroke="#fff"
                    strokeWidth="2"
                    className="hover:opacity-80 transition-opacity duration-200 cursor-pointer"
                  >
                    <title>{`${name}: ${value} (${percentage.toFixed(1)}%)`}</title>
                  </path>
                );
              })}
            </svg>

            {/* Center text */}
            <div className="absolute inset-0 flex flex-col items-center justify-center">
              <div className="text-2xl font-bold text-gray-900">{total}</div>
              <div className="text-sm text-gray-600">{centerText}</div>
            </div>
          </div>

          <div className="ml-8 space-y-3">
            {entries.map(([name, value], index) => {
              const percentage = ((value / total) * 100).toFixed(1);
              return (
                <div key={index} className="flex items-center space-x-3">
                  <div 
                    className="w-4 h-4 rounded-full"
                    style={{ backgroundColor: colors[index % colors.length] }}
                  />
                  <div className="flex-1">
                    <div className="flex items-center justify-between">
                      <span className="text-sm font-medium text-gray-900">{name}</span>
                      <span className="text-sm text-gray-600">{value}</span>
                    </div>
                    <div className="text-xs text-gray-500">{percentage}% of total</div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    );
  };

  // Professional Bar Chart Component
  const BarChart = ({ data, title, showTrend = false }) => {
    if (!data || data.length === 0) return <div className="text-gray-500">No data available</div>;

    const maxValue = Math.max(...data.map(item => item.count));

    return (
      <div className="bg-white rounded-xl border border-gray-200 p-6 shadow-sm">
        <h3 className="text-lg font-semibold text-gray-900 mb-6">{title}</h3>
        <div className="space-y-4">
          {data.map((item, index) => {
            const percentage = maxValue > 0 ? (item.count / maxValue) * 100 : 0;
            return (
              <div key={index} className="space-y-2">
                <div className="flex items-center justify-between">
                  <span className="text-sm font-medium text-gray-700">{item.week}</span>
                  <span className="text-sm font-semibold text-gray-900">{item.count}</span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div 
                    className="bg-gradient-to-r from-blue-500 to-blue-600 h-2 rounded-full transition-all duration-500 ease-out"
                    style={{ width: `${percentage}%` }}
                  />
                </div>
              </div>
            );
          })}
        </div>
      </div>
    );
  };

  // Team Workload Component
  const TeamWorkload = () => {
    const teamData = [
      { name: "Alice Johnson", assigned: 15, completed: 12, efficiency: 80 },
      { name: "Bob Smith", assigned: 20, completed: 18, efficiency: 90 },
      { name: "Carol Davis", assigned: 12, completed: 10, efficiency: 83 },
      { name: "David Wilson", assigned: 18, completed: 14, efficiency: 78 }
    ];

    return (
      <div className="bg-white rounded-xl border border-gray-200 p-6 shadow-sm">
        <h3 className="text-lg font-semibold text-gray-900 mb-6">Team Workload Distribution</h3>
        <div className="space-y-6">
          {teamData.map((member, index) => (
            <div key={index} className="space-y-2">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full flex items-center justify-center">
                    <span className="text-white text-sm font-semibold">{member.name.charAt(0)}</span>
                  </div>
                  <span className="text-sm font-medium text-gray-900">{member.name}</span>
                </div>
                <div className="text-right">
                  <div className="text-sm font-semibold text-gray-900">{member.completed}/{member.assigned}</div>
                  <div className="text-xs text-gray-500">{member.efficiency}% efficiency</div>
                </div>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div 
                  className="bg-gradient-to-r from-green-500 to-green-600 h-2 rounded-full transition-all duration-500"
                  style={{ width: `${member.efficiency}%` }}
                />
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <RefreshCw className="w-12 h-12 text-blue-600 animate-spin mx-auto mb-4" />
          <h3 className="text-lg font-semibold text-gray-900 mb-2">Loading Analytics</h3>
          <p className="text-gray-600">Fetching data from server...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center p-4">
        <div className="bg-white rounded-xl border border-red-200 p-8 max-w-md w-full text-center shadow-lg">
          <div className="w-16 h-16 bg-red-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <Target className="w-8 h-8 text-red-600" />
          </div>
          <h3 className="text-lg font-semibold text-gray-900 mb-2">Analytics Error</h3>
          <p className="text-gray-600 mb-6">{error}</p>
          <div className="space-y-3">
            <button 
              onClick={handleRetry}
              className="w-full bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 transition-colors duration-200 flex items-center justify-center space-x-2"
            >
              <RefreshCw className="w-4 h-4" />
              <span>Retry</span>
            </button>
            <button 
              onClick={handleBackNavigation}
              className="w-full bg-gray-100 text-gray-700 py-2 px-4 rounded-lg hover:bg-gray-200 transition-colors duration-200"
            >
              ← Back to Dashboard
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          {/* Breadcrumbs */}
          <div className="flex items-center space-x-2 py-4 text-sm text-gray-600">
            <Home className="w-4 h-4" />
            <span>Dashboard</span>
            <ChevronRight className="w-4 h-4" />
            <span className="text-gray-900 font-medium">Analytics</span>
          </div>

          {/* Title and Navigation */}
          <div className="flex items-center justify-between pb-6">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">Analytics Dashboard</h1>
              <p className="mt-2 text-gray-600">Comprehensive insights into your ticket management performance</p>
            </div>

            <div className="flex items-center space-x-4">
              <div className="flex bg-gray-100 rounded-lg p-1">
                <button
                  onClick={() => setActiveView('overview')}
                  className={`flex items-center space-x-2 px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                    activeView === 'overview' 
                      ? 'bg-white text-blue-600 shadow-sm' 
                      : 'text-gray-600 hover:text-gray-900'
                  }`}
                >
                  <Eye className="w-4 h-4" />
                  <span>Overview</span>
                </button>
                <button
                  onClick={() => setActiveView('trends')}
                  className={`flex items-center space-x-2 px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                    activeView === 'trends' 
                      ? 'bg-white text-blue-600 shadow-sm' 
                      : 'text-gray-600 hover:text-gray-900'
                  }`}
                >
                  <Activity className="w-4 h-4" />
                  <span>Trends</span>
                </button>
              </div>

              <button 
                onClick={handleBackNavigation}
                className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition-colors duration-200 flex items-center space-x-2"
              >
                <span>← Back</span>
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {activeView === 'overview' ? (
          <div className="space-y-8">
            {/* KPI Cards */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              <KPICard
                title="Total Tickets"
                value={metrics.totalTickets}
                subtitle="All time"
                icon={BarChart3}
                trend={metrics.trend}
                trendValue={metrics.trendPercentage}
                color="blue"
              />
              <KPICard
                title="Resolved Tickets"
                value={metrics.resolvedTickets}
                subtitle={`${metrics.completionRate.toFixed(1)}% completion rate`}
                icon={Target}
                color="green"
              />
              <KPICard
                title="Active Tickets"
                value={metrics.activeTickets}
                subtitle="Currently open"
                icon={Clock}
                color="orange"
              />
              <KPICard
                title="Avg Resolution Time"
                value={`${metrics.avgResolutionTime} days`}
                subtitle="Time to resolve"
                icon={TrendingUp}
                color="purple"
              />
            </div>

            {/* Charts Grid */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <DonutChart 
                data={analytics.priority_distribution} 
                title="Priority Distribution"
                centerText="Total Tickets"
              />
              <TeamWorkload />
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-1 gap-6">
              <BarChart 
                data={analytics.tickets_per_week} 
                title="Weekly Ticket Creation"
              />
            </div>
          </div>
        ) : (
          <div className="space-y-8">
            {/* Trends View */}
            <div className="bg-white rounded-xl border border-gray-200 p-6 shadow-sm">
              <h2 className="text-xl font-semibold text-gray-900 mb-6">Trend Analysis</h2>
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                <div className="space-y-4">
                  <h3 className="text-lg font-medium text-gray-900">Weekly Performance</h3>
                  <div className="space-y-4">
                    {analytics.tickets_per_week?.slice(-4).map((week, index) => (
                      <div key={index} className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                        <span className="font-medium">{week.week}</span>
                        <div className="flex items-center space-x-4">
                          <span className="text-blue-600 font-semibold">{week.count} created</span>
                          <span className="text-green-600 font-semibold">{Math.floor(week.count * 0.85)} resolved</span>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="space-y-4">
                  <h3 className="text-lg font-medium text-gray-900">Key Insights</h3>
                  <div className="space-y-3">
                    <div className="p-4 bg-blue-50 rounded-lg border border-blue-200">
                      <div className="flex items-center space-x-2">
                        <TrendingUp className="w-5 h-5 text-blue-600" />
                        <span className="font-medium text-blue-900">Velocity Trend</span>
                      </div>
                      <p className="text-blue-700 mt-2">Team is maintaining steady resolution pace</p>
                    </div>
                    <div className="p-4 bg-green-50 rounded-lg border border-green-200">
                      <div className="flex items-center space-x-2">
                        <Target className="w-5 h-5 text-green-600" />
                        <span className="font-medium text-green-900">High Performance</span>
                      </div>
                      <p className="text-green-700 mt-2">85% completion rate exceeds target</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
