.jira-alert-dismiss {
  position: absolute;
  top: 0.75rem;
  right: 0.75rem;
  background: none;
  border: none;
  color: inherit;
  cursor: pointer;
  padding: 0.25rem;
  border-radius: 0.25rem;
}

.jira-spinner {
  border: 2px solid #f3f4f6;
  border-top: 2px solid #3b82f6;
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

.jira-spinner-sm {
  width: 16px;
  height: 16px;
}

.jira-spinner-md {
  width: 24px;
  height: 24px;
}

.jira-spinner-lg {
  width: 32px;
  height: 32px;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

.jira-empty-state {
  text-align: center;
  padding: 3rem 1rem;
  color: #64748b;
}

.jira-empty-state svg {
  margin: 0 auto 1rem;
  color: #cbd5e1;
}

.jira-empty-state h3 {
  font-size: 1.125rem;
  font-weight: 600;
  color: #374151;
  margin-bottom: 0.5rem;
}

.jira-column-empty {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 2rem;
  color: #9ca3af;
  min-height: 120px;
}

.jira-footer {
  background: white;
  border-top: 1px solid #e2e8f0;
  padding: 1.5rem 0;
  margin-top: 2rem;
  text-align: center;
  font-size: 0.875rem;
  color: #64748b;
}

.jira-status-text {
  margin-left: 0.5rem;
  font-weight: 500;
}

.jira-status-text.connected {
  color: #16a34a;
}

.jira-status-text.disconnected {
  color: #f59e0b;
}

.jira-status-text.error {
  color: #dc2626;
}

.jira-flex {
  display: flex;
}

.jira-items-center {
  align-items: center;
}

.jira-justify-center {
  justify-content: center;
}

.jira-gap-2 {
  gap: 0.5rem;
}

.jira-gap-3 {
  gap: 0.75rem;
}

.jira-py-12 {
  padding: 3rem 0;
}

.jira-mb-4 {
  margin-bottom: 1rem;
}

.jira-min-w-0 {
  min-width: 0;
}

.jira-flex-1 {
  flex: 1;
}

.jira-w-full {
  width: 100%;
}

.animate-spin {
  animation: spin 1s linear infinite;
}

.ticket-details {
  max-height: 400px;
  overflow-y: auto;
}

.ticket-field {
  margin-bottom: 1rem;
  padding-bottom: 1rem;
  border-bottom: 1px solid #e2e8f0;
}

.ticket-field:last-child {
  border-bottom: none;
}

.ticket-field-label {
  font-weight: 600;
  color: #374151;
  margin-bottom: 0.5rem;
  font-size: 0.875rem;
}

.ticket-field-value {
  color: #64748b;
  font-size: 0.875rem;
  line-height: 1.5;
}

.ticket-field-value.empty {
  font-style: italic;
  color: #9ca3af;
}

.done-comment-section {
  border-top: 1px solid #e2e8f0;
  padding-top: 1rem;
  margin-top: 1rem;
}

.done-comment-label {
  font-size: 0.875rem;
  font-weight: 500;
  color: #374151;
  margin-bottom: 0.5rem;
  display: block;
}

.done-comment-input {
  width: 100%;
  padding: 0.75rem;
  border: 1px solid #d1d5db;
  border-radius: 0.375rem;
  font-size: 0.875rem;
  resize: vertical;
  min-height: 80px;
}

.done-comment-input:focus {
  outline: none;
  border-color: #3b82f6;
  box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
}

.form-compact {
  padding: 1rem;
}

.form-compact .jira-form-actions {
  margin-top: 1rem;
  padding-top: 1rem;
  border-top: 1px solid #e2e8f0;
}

.readonly-field {
  background-color: #f8fafc;
  color: #64748b;
  cursor: not-allowed;
}

@media (max-width: 768px) {
  .jira-filters {
    flex-direction: column;
    align-items: stretch;
  }

  .jira-filters-left {
    flex-direction: column;
  }

  .jira-filters-right {
    min-width: auto;
  }

  .jira-form-row,
  .jira-form-row-3 {
    grid-template-columns: 1fr;
  }
}
