      setError('');
      const data = await apiService.getTickets(currentFilters);
      setTickets(data);
    } catch (err) {
      setError(err.message || 'Impossible de charger les tickets');
      setTickets({});
    } finally {
      setLoading(false);
    }
  }, []);  

  useEffect(() => {
    checkHealth();
    loadFilterOptions(); // Charger les options de filtres au démarrage
    loadTickets(filters);
  }, [checkHealth, loadFilterOptions, filters]);

  const loadAnalytics = async () => {
