  const getOrderedStatuses = (statuses) => {
    const statusOrder = {
      'À faire': 1,
      'A faire': 1,
      'Todo': 1,
      'TO DO': 1,
      'Open': 1,
      'En cours': 2,
      'IN PROGRESS': 2,
      'In Progress': 2,
      'Progress': 2,
      'Terminé': 3,
      'Terminée': 3,
      'Terminées': 3,
      'Done': 3,
      'DONE': 3,
      'Closed': 3,
      'Resolved': 3
    };

    return statuses.sort((a, b) => {
      const orderA = statusOrder[a] || 999;
      const orderB = statusOrder[b] || 999;

      if (orderA !== orderB) {
        return orderA - orderB;
      }

      // Si même ordre, tri alphabétique
      return a.localeCompare(b);
