  line-height: 1.4;
}

.jira-ticket-actions {
  display: flex;
  gap: 0.25rem;
  flex-shrink: 0;
  opacity: 0;
  transition: opacity 0.2s;
}

.jira-ticket-card:hover .jira-ticket-actions {
  opacity: 1;
}

.jira-ticket-action {
  padding: 0.25rem;
  border: none;
  background: none;
  color: #64748b;
  border-radius: 0.25rem;
  cursor: pointer;
  transition: all 0.2s;
}

.jira-ticket-action:hover {
  background-color: #f1f5f9;
  color: #334155;
}

.jira-ticket-action.success:hover {
  background-color: #dcfce7;
  color: #16a34a;
}

.jira-ticket-action.danger:hover {
  background-color: #fef2f2;
  color: #dc2626;
}

.jira-status-badge {
  display: inline-flex;
  align-items: center;
  gap: 0.25rem;
  padding: 0.25rem 0.5rem;
  border-radius: 0.25rem;
  font-size: 0.75rem;
  font-weight: 500;
}

.jira-status-todo {
  background-color: #fef3c7;
  color: #92400e;
}

.jira-status-progress {
  background-color: #dbeafe;
  color: #1e40af;
}

.jira-status-done {
  background-color: #dcfce7;
  color: #166534;
}

.jira-status-other {
  background-color: #f3f4f6;
  color: #374151;
}

.jira-modal-overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  padding: 1rem;
}

.jira-modal {
  background: white;
  border-radius: 0.5rem;
  max-width: 600px;
  width: 100%;
  max-height: 90vh;
  overflow: hidden;
  display: flex;
  flex-direction: column;
}

.jira-modal-header {
  padding: 1.5rem;
  border-bottom: 1px solid #e2e8f0;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.jira-modal-title {
  font-size: 1.25rem;
  font-weight: 600;
  color: #1e293b;
  margin: 0;
}

.jira-modal-close {
  background: none;
  border: none;
  color: #64748b;
  cursor: pointer;
  padding: 0.25rem;
  border-radius: 0.25rem;
}

.jira-modal-close:hover {
  background-color: #f1f5f9;
  color: #334155;
}

.jira-modal-body {
  padding: 1.5rem;
  overflow-y: auto;
  flex: 1;
}

.jira-form-group {
  margin-bottom: 1rem;
}

.jira-form-label {
  display: block;
  font-size: 0.875rem;
  font-weight: 500;
  color: #374151;
  margin-bottom: 0.5rem;
}

.jira-form-row {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1rem;
}

.jira-form-row-3 {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  gap: 1rem;
}

.jira-form-actions {
  display: flex;
  gap: 0.75rem;
  justify-content: flex-end;
  margin-top: 1.5rem;
}

.jira-form-error {
  color: #dc2626;
  font-size: 0.75rem;
  margin-top: 0.25rem;
}

.jira-alert {
  display: flex;
  align-items: flex-start;
  gap: 0.75rem;
  padding: 1rem;
  border-radius: 0.5rem;
  margin-bottom: 1rem;
}

.jira-alert-error {
  background-color: #fef2f2;
  color: #991b1b;
  border: 1px solid #fecaca;
}

.jira-alert-warning {
  background-color: #fffbeb;
  color: #92400e;
  border: 1px solid #fed7aa;
}

.jira-alert-dismissible {
  position: relative;
}

.jira-alert-dismiss {
  position: absolute;
  top: 0.75rem;
  right: 0.75rem;
  background: none;
  border: none;
  color: inherit;
  cursor: pointer;
  padding: 0.25rem;
  border-radius: 0.25rem;
}

.jira-spinner {
  border: 2px solid #f3f4f6;
  border-top: 2px solid #3b82f6;
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

.jira-spinner-sm {
  width: 16px;
  height: 16px;
}

.jira-spinner-md {
  width: 24px;
  height: 24px;
}

.jira-spinner-lg {
  width: 32px;
  height: 32px;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

.jira-empty-state {
  text-align: center;
  padding: 3rem 1rem;
  color: #64748b;
}

.jira-empty-state svg {
  margin: 0 auto 1rem;
  color: #cbd5e1;
}

.jira-empty-state h3 {
  font-size: 1.125rem;
  font-weight: 600;
  color: #374151;
  margin-bottom: 0.5rem;
}

.jira-column-empty {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 2rem;
  color: #9ca3af;
  min-height: 120px;
}

.jira-footer {
  background: white;
  border-top: 1px solid #e2e8f0;
  padding: 1.5rem 0;
  margin-top: 2rem;
  text-align: center;
  font-size: 0.875rem;
  color: #64748b;
}

.jira-status-text {
  margin-left: 0.5rem;
  font-weight: 500;
}

.jira-status-text.connected {
  color: #16a34a;
}

.jira-status-text.disconnected {
  color: #f59e0b;
}

.jira-status-text.error {
  color: #dc2626;
}

.jira-flex {
  display: flex;
}

.jira-items-center {
  align-items: center;
}

.jira-justify-center {
  justify-content: center;
}

.jira-gap-2 {
  gap: 0.5rem;
}

.jira-gap-3 {
  gap: 0.75rem;
}

.jira-py-12 {
  padding: 3rem 0;
}

.jira-mb-4 {
  margin-bottom: 1rem;
}

.jira-min-w-0 {
  min-width: 0;
}

.jira-flex-1 {
  flex: 1;
}

.jira-w-full {
  width: 100%;
}

.animate-spin {
  animation: spin 1s linear infinite;
}

.ticket-details {
  max-height: 400px;
  overflow-y: auto;
}

.ticket-field {
  margin-bottom: 1rem;
  padding-bottom: 1rem;
  border-bottom: 1px solid #e2e8f0;
}

.ticket-field:last-child {
  border-bottom: none;
}

.ticket-field-label {
  font-weight: 600;
  color: #374151;
  margin-bottom: 0.5rem;
  font-size: 0.875rem;
}

.ticket-field-value {
  color: #64748b;
  font-size: 0.875rem;
  line-height: 1.5;
}

.ticket-field-value.empty {
  font-style: italic;
  color: #9ca3af;
}

.done-comment-section {
  border-top: 1px solid #e2e8f0;
  padding-top: 1rem;
  margin-top: 1rem;
}

.done-comment-label {
  font-size: 0.875rem;
  font-weight: 500;
  color: #374151;
  margin-bottom: 0.5rem;
  display: block;
}

.done-comment-input {
  width: 100%;
  padding: 0.75rem;
  border: 1px solid #d1d5db;
  border-radius: 0.375rem;
  font-size: 0.875rem;
  resize: vertical;
  min-height: 80px;
}

.done-comment-input:focus {
  outline: none;
  border-color: #3b82f6;
  box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
}

.form-compact {
  padding: 1rem;
}

.form-compact .jira-form-actions {
  margin-top: 1rem;
  padding-top: 1rem;
  border-top: 1px solid #e2e8f0;
}

.readonly-field {
  background-color: #f8fafc;
  color: #64748b;
  cursor: not-allowed;
}

@media (max-width: 768px) {
  .jira-filters {
    flex-direction: column;
    align-items: stretch;
  }

  .jira-filters-left {
    flex-direction: column;
  }

  .jira-filters-right {
    min-width: auto;
  }

  .jira-form-row,
  .jira-form-row-3 {
    grid-template-columns: 1fr;
  }
}
      `}</style>
      <div className="jira-manager">
        {/* Header */}
        <header className="jira-header">
          <div className="jira-header-content">
            <div className="jira-header-left">
              <h1 className="jira-header-title">Jira Manager Pro</h1>
              <div className="jira-header-stats">
                <span>{totalTickets} tickets</span>
                <span>•</span>
                <span>{statuses.length} statuts</span>
                <span>•</span>
                <div className="jira-flex jira-items-center jira-gap-2">
                  <div className={`jira-status-indicator ${
                    connectionStatus === 'connected' ? '' : 
                    connectionStatus === 'disconnected' ? 'disconnected' : 'error'
                  }`}></div>
                  <span>
                    {connectionStatus === 'connected' ? 'Connecté' : 
                     connectionStatus === 'disconnected' ? 'Déconnecté' : 'Erreur'}
                  </span>
                </div>
              </div>
            </div>

            <div className="jira-header-actions">
              <button
                onClick={() => { checkHealth(); loadTickets(); }}
                disabled={loading}
                className="jira-btn jira-btn-secondary jira-btn-icon"
                title="Actualiser"
              >
                <RefreshCw size={18} className={loading ? 'animate-spin' : ''} />
              </button>

              <button
                onClick={() => openModal('create')}
                className="jira-btn jira-btn-primary"
                disabled={connectionStatus !== 'connected'}
              >
                <Plus size={18} />
                Nouveau ticket
              </button>
              <button
                onClick={() => window.location.href = '/analytics'}
                className="jira-btn jira-btn-secondary"
              >
                📊 Analyses
              </button>
            </div>
          </div>
        </header>

        {/* Connection Warning */}
        {connectionStatus !== 'connected' && (
          <div className="jira-container">
            <div className="jira-alert jira-alert-warning">
              <AlertCircle size={20} />
              <div>
                <p style={{fontWeight: 500}}>
                  {connectionStatus === 'disconnected' ? 'Connexion Jira déconnectée' : 'Erreur de connexion'}
                </p>
                <p style={{fontSize: '0.875rem'}}>
                  Vérifiez que l'API est démarrée et que la configuration Jira est correcte.
                </p>
              </div>
            </div>
          </div>
        )}
        {showAnalytics && (
        <div className="mt-4">
          <button onClick={() => setShowAnalytics(false)} className="jira-btn jira-btn-secondary">
            Close Analytics
          </button>
          <AnalyticsDashboard />
        </div>
      )}
        {/* Filters */}
        <div className="jira-container">
          <div className="jira-card jira-mb-4">
            <div className="jira-card-body">
              <div className="jira-filters">
                <div className="jira-filters-left">
                  {/* Issue Type Filter - utilise maintenant filterOptions.types */}
                  <div className="jira-filter-group">
                    <Tag size={18} style={{color: '#64748b'}} />
                    <select
                      value={filters.type}
                      onChange={(e) => handleFilterChange({ ...filters, type: e.target.value })}
                      className="jira-form-select"
                    >
                      <option value="all">Tous les types</option>
                      {filterOptions.types.map(type => (
                        <option key={type} value={type}>{type}</option>
                      ))}
                    </select>
                  </div>

                  {/* Priority Filter - utilise maintenant filterOptions.priorities */}
                  <div className="jira-filter-group">
                    <AlertCircle size={18} style={{color: '#64748b'}} />
                    <select
                      value={filters.priority}
                      onChange={(e) => handleFilterChange({ ...filters, priority: e.target.value })}
                      className="jira-form-select"
                    >
                      <option value="all">Toutes les priorités</option>
                      {filterOptions.priorities.map(priority => (
                        <option key={priority} value={priority}>{priority}</option>
                      ))}
                    </select>
                  </div>

                  {/* Status Filter - utilise maintenant filterOptions.statuses */}
                  <div className="jira-filter-group">
                    <CheckCircle size={18} style={{color: '#64748b'}} />
                    <select
                      value={filters.status}
                      onChange={(e) => handleFilterChange({ ...filters, status: e.target.value })}
                      className="jira-form-select"
                    >
                      <option value="all">Tous les statuts</option>
                      {filterOptions.statuses.map(status => (
                        <option key={status} value={status}>{status}</option>
                      ))}
                    </select>
                  </div>

                  {/* Assignee Filter - utilise maintenant filterOptions.assignees */}
                  <div className="jira-filter-group">
                    <User size={18} style={{color: '#64748b'}} />
                    <select
                      value={filters.assignee}
                      onChange={(e) => handleFilterChange({ ...filters, assignee: e.target.value === 'unassigned' ? 'unassigned' : e.target.value })}
                      className="jira-form-select"
                    >
                      <option value="all">Tous les assignés</option>
                      {filterOptions.assignees.map(assignee => (
                        <option key={assignee} value={assignee}>{assignee}</option>
                      ))}
                    </select>
                  </div>
                </div>

                <div className="jira-filters-right">
                  <div className="jira-filter-group">
                    <Search size={18} style={{color: '#64748b'}} />
                    <input
                      type="text"
                      placeholder="Rechercher des tickets..."
                      value={filters.search}
                      onChange={(e) => handleFilterChange({ ...filters, search: e.target.value })}
                      className="jira-form-input"
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Error Message */}
          {error && (
            <div className="jira-alert jira-alert-error jira-alert-dismissible">
              <AlertCircle size={20} />
              <span>{error}</span>
              <button
                onClick={() => setError('')}
                className="jira-alert-dismiss"
              >
                <X size={18} />
              </button>
            </div>
          )}

          {/* Loading State */}
          {loading ? (
            <div className="jira-flex jira-items-center jira-justify-center jira-py-12">
              <LoadingSpinner size="lg" />
              <span style={{marginLeft: '0.75rem', color: '#64748b'}}>Chargement des tickets...</span>
            </div>
          ) : (
            /* Tickets Grid */
            <div className="jira-grid jira-grid-3">
              {statuses.length === 0 ? (
                <div className="jira-empty-state" style={{gridColumn: '1 / -1'}}>
                  <Tag size={48} />
                  <h3>Aucun ticket trouvé</h3>
                  <p>
                    {connectionStatus === 'connected' 
                      ? 'Créez votre premier ticket pour commencer' 
                      : 'Vérifiez la connexion à Jira'}
                  </p>
                  {connectionStatus === 'connected' && (
                    <button
                      onClick={() => openModal('create')}
                      className="jira-btn jira-btn-primary"
                    >
                      <Plus size={18} />
                      Créer un ticket
                    </button>
                  )}
                </div>
              ) : (
                statuses.map(status => (
                  <div key={status} className="jira-column">
                    <div className="jira-column-header">
                      <StatusBadge status={status} />
                      <span className="jira-column-count">
                        {tickets[status]?.length || 0}
                      </span>
                    </div>

                    <div className="jira-column-body">
                      {tickets[status]?.length === 0 ? (
                        <div className="jira-column-empty">
                          <Clock size={24} />
                          <p>Aucun ticket</p>
                        </div>
                      ) : (
                        tickets[status]?.map((ticket, index) => {
                          const ticketInfo = parseTicketInfo(ticket);
                          const ticketKey = ticketInfo ? ticketInfo.key : ticket.split(':')[0].trim();
                          return (
                            <TicketCard
                              key={`${ticketKey}-${index}`}
                              ticket={ticket}
                              onEdit={(key) => openModal('edit', key)}
                              onDelete={(key) => {
                                setConfirmAction({
                                  ticketKey: key,
                                  title: 'Supprimer le ticket',
                                  message: `Êtes-vous sûr de vouloir supprimer le ticket ${key} ? Cette action est irréversible.`
                                });
                                openModal('confirm');
                              }}
                              onTransition={(key) => openModal('transition', key)}
                            />
                          );
                        })
                      )}
                    </div>
                  </div>
                ))
              )}
            </div>
          )}
        </div>

        {/* Modals - maintenant avec les options fixes */}
        <CreateTicketModal
          isOpen={modals.create}
          onClose={() => closeModal('create')}
          onSubmit={handleCreateTicket}
          loading={actionLoading}
          assignees={filterOptions.assignees}
          types={filterOptions.types}
          priorities={filterOptions.priorities}
        />

        <EditTicketModal
          isOpen={modals.edit}
          onClose={() => closeModal('edit')}
          onSubmit={handleEditTicket}
          loading={actionLoading}
          ticketKey={selectedTicket}
          assignees={filterOptions.assignees}
          types={filterOptions.types}
          priorities={filterOptions.priorities}
        />

        <TransitionModal
          isOpen={modals.transition}
          onClose={() => closeModal('transition')}
          onSubmit={handleTransitionTicket}
          loading={actionLoading}
          ticketKey={selectedTicket}
          currentStatus={selectedTicket ? findTicketStatus(selectedTicket) : null}
        />

        <ConfirmModal
          isOpen={modals.confirm}
          onClose={() => closeModal('confirm')}
          onConfirm={handleDeleteTicket}
          loading={actionLoading}
          title={confirmAction?.title || ''}
          message={confirmAction?.message || ''}
        />

        {/* Footer */}
        <footer className="jira-footer">
          <div className="jira-container">
            <p>Jira Manager Pro - Interface moderne pour la gestion de tickets</p>
            <p>
              API Status: 
              <span className={`jira-status-text ${
                connectionStatus === 'connected' ? 'connected' : 
                connectionStatus === 'disconnected' ? 'disconnected' : 'error'
              }`}>
                {connectionStatus === 'connected' ? 'Connecté à Jira' : 
                 connectionStatus === 'disconnected' ? 'Jira déconnecté' : 'Erreur de connexion'}
              </span>
            </p>
          </div>
        </footer>
      </div>
    </>
  );
}
