// src/AnalyticsDashboard.jsx
import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, PieChart, Pie, Cell, ResponsiveContainer } from 'recharts';

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];

export default function AnalyticsDashboard() {
  const [analytics, setAnalytics] = useState({
    tickets_per_week: [],
    priority_distribution: {},
    avg_resolution_time: 0.0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAnalytics = async () => {
        try {
        const response = await fetch('/api/analytics');
        if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
        const text = await response.text(); // Get raw response
        console.log('Raw response:', text); // Log for debugging
        const data = JSON.parse(text); // Attempt to parse
        setAnalytics(data);
        } catch (err) {
        setError(err.message);
        console.error('Fetch error:', err);
        } finally {
        setLoading(false);
        }
    };
    fetchAnalytics();
    }, []);

  if (loading) return <div>Loading analytics...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>Analytics Dashboard</h2>
      <h3>Ticket Count per Week</h3>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={analytics.tickets_per_week}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="week" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="count" fill="#8884d8" />
        </BarChart>
      </ResponsiveContainer>

      <h3>Priority Distribution</h3>
      <ResponsiveContainer width="100%" height={300}>
        <PieChart>
          <Pie
            data={Object.entries(analytics.priority_distribution).map(([key, value]) => ({ name: key, value }))}
            dataKey="value"
            nameKey="name"
            cx="50%"
            cy="50%"
            outerRadius={100}
            fill="#8884d8"
            label
          >
            {Object.entries(analytics.priority_distribution).map((entry, index) => (
              <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
            ))}
          </Pie>
          <Tooltip />
          <Legend />
        </PieChart>
      </ResponsiveContainer>

      <h3>Average Resolution Time</h3>
      <div style={{ padding: '10px', border: '1px solid #ccc', width: '200px', textAlign: 'center' }}>
        {analytics.avg_resolution_time} days
      </div>

      <button onClick={() => window.history.back()}>Back to Main</button>
    </div>
  );
}
