import React, { useState, useEffect, useCallback } from 'react';
import {
  ArrowLeft,
  TrendingUp,
  Clock,
  PieChart,
  BarChart3,
  RefreshCw,
} from 'lucide-react';
import {
  PieChart as RechartsPieChart,
  Pie,
  Cell,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
} from 'recharts';

const API_BASE_URL = 'http://localhost:5000/api';
const COLORS = ['#8b5cf6', '#a855f7', '#c084fc', '#d8b4fe', '#e9d5ff'];

const AnalyticsCard = ({ title, value, icon: Icon, color = 'bg-indigo-100' }) => (
  <div className={`p-6 rounded-lg ${color} shadow-sm border border-indigo-200`}>
    <div className="flex items-center space-x-3">
      <div className="p-2 rounded-full bg-white bg-opacity-75">
        <Icon className="h-6 w-6 text-indigo-600" />
      </div>
      <h3 className="text-sm font-semibold text-gray-700">{title}</h3>
    </div>
    <p className="mt-4 text-3xl font-bold text-gray-900">{value}</p>
  </div>
);

const NoData = () => (
  <div className="flex flex-col items-center justify-center p-8 bg-gray-50 rounded-lg shadow-sm">
    <AlertCircle className="h-10 w-10 text-yellow-500 mb-4" />
    <p className="text-gray-600 font-medium text-center">
      Pas de données disponibles pour les graphiques.
    </p>
    <p className="text-sm text-gray-500 mt-1 text-center">
      Assurez-vous que des tickets existent dans votre projet Jira.
    </p>
  </div>
);

export default function AnalyticsDashboard({ onBack }) {
  const [analytics, setAnalytics] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchAnalytics = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await fetch(`${API_BASE_URL}/analytics`);
      if (!response.ok) {
        throw new Error(`Erreur HTTP ${response.status}: ${response.statusText}`);
      }
      const data = await response.json();
      setAnalytics(data);
    } catch (err) {
      console.error('Erreur de récupération des analytics:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchAnalytics();
  }, [fetchAnalytics]);

  const pieData = analytics ? Object.entries(analytics.tickets_par_type).map(([name, value]) => ({ name, value })) : [];
  const barData = analytics ? analytics.tickets_par_semaine : [];

  return (
    <div className="p-8 bg-gray-50 min-h-screen">
      <div className="max-w-7xl mx-auto">
        <header className="flex items-center justify-between mb-8">
          <h1 className="text-3xl font-bold text-gray-900">
            Tableau de bord des Données
          </h1>
          <div className="flex items-center space-x-4">
            <button
              onClick={onBack}
              className="flex items-center px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-100"
            >
              <ArrowLeft className="h-5 w-5 mr-2" /> Retour
            </button>
            <button
              onClick={fetchAnalytics}
              className="flex items-center px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-100"
            >
              <RefreshCw className="h-5 w-5 mr-2" /> Actualiser
            </button>
          </div>
        </header>

        {loading && (
          <div className="flex justify-center items-center py-20">
            <div className="w-12 h-12 border-4 border-t-4 border-gray-200 border-t-indigo-500 rounded-full animate-spin"></div>
          </div>
        )}

        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-md relative" role="alert">
            <strong className="font-bold">Erreur de connexion :</strong>
            <span className="block sm:inline ml-2">{error}. Veuillez vérifier l'API.</span>
          </div>
        )}

        {analytics && !loading && (
          <>
            <section className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-10">
              <AnalyticsCard
                title="Temps Moyen de Résolution"
                value={analytics.temps_moyen_resolution ? `${analytics.temps_moyen_resolution}h` : 'N/A'}
                icon={Clock}
                color="bg-green-100"
              />
              <AnalyticsCard
                title="Temps Moyen de Cycle"
                value={analytics.temps_moyen_cycle ? `${analytics.temps_moyen_cycle}h` : 'N/A'}
                icon={Clock}
                color="bg-blue-100"
              />
              <AnalyticsCard
                title="Type le plus fréquent"
                value={analytics.tickets_par_type && Object.keys(analytics.tickets_par_type).length > 0 ? Object.keys(analytics.tickets_par_type)[0] : 'N/A'}
                icon={BarChart3}
                color="bg-yellow-100"
              />
              <AnalyticsCard
                title="Priorité la plus fréquente"
                value={analytics.tickets_par_priorite && Object.keys(analytics.tickets_par_priorite).length > 0 ? Object.keys(analytics.tickets_par_priorite)[0] : 'N/A'}
                icon={TrendingUp}
                color="bg-purple-100"
              />
            </section>

            <section className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              {/* Graphique de Distribution des Types */}
              <div className="bg-white p-6 rounded-lg shadow-lg">
                <h2 className="text-xl font-bold mb-4 text-gray-800">Distribution par Type de Ticket</h2>
                {pieData.length > 0 ? (
                  <ResponsiveContainer width="100%" height={300}>
                    <RechartsPieChart>
                      <Pie
                        data={pieData}
                        dataKey="value"
                        nameKey="name"
                        cx="50%"
                        cy="50%"
                        outerRadius={80}
                        label={({ name, percent }) => `${name} (${(percent * 100).toFixed(0)}%)`}
                      >
                        {pieData.map((entry, index) => (
                          <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                        ))}
                      </Pie>
                      <Tooltip />
                    </RechartsPieChart>
                  </ResponsiveContainer>
                ) : <NoData />}
              </div>

              {/* Graphique des Tickets créés par Semaine */}
              <div className="bg-white p-6 rounded-lg shadow-lg">
                <h2 className="text-xl font-bold mb-4 text-gray-800">Tickets Créés par Semaine</h2>
                {barData.length > 0 ? (
                  <ResponsiveContainer width="100%" height={300}>
                    <BarChart data={barData} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
                      <XAxis dataKey="week" />
                      <YAxis />
                      <Tooltip />
                      <Bar dataKey="tickets" fill="#8b5cf6" />
                    </BarChart>
                  </ResponsiveContainer>
                ) : <NoData />}
              </div>
            </section>
          </>
        )}
      </div>
    </div>
  );
}
