import React, { useState, useEffect, useMemo } from 'react';
import { ArrowLeft, TrendingUp, TrendingDown, Clock, CheckCircle, AlertCircle, PieChart, BarChart3, Calendar, Users, RefreshCw } from 'lucide-react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, PieChart as RechartsPieChart, Pie, Cell, ResponsiveContainer, ScatterChart, Scatter, RadialBarChart, RadialBar } from 'recharts';

const COLORS = ['#667eea', '#764ba2', '#10B981', '#F59E0B', '#EF4444', '#8B5CF6', '#EC4899'];

export default function AnalyticsDashboard() {
  const [analytics, setAnalytics] = useState({
    tickets_per_week: [],
    priority_distribution: {},
    type_distribution: {},
    avg_resolution_time: 0.0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeView, setActiveView] = useState('overview');
  const [timeFilter, setTimeFilter] = useState('all'); // Filtre temporel interactif

  useEffect(() => {
    fetchAnalytics();
  }, [timeFilter]);

  const fetchAnalytics = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:5000/api/analytics', {
        method: 'GET',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
        },
        mode: 'cors'
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();
      setAnalytics(data);
    } catch (err) {
      console.error('Analytics fetch error:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleBackToMain = () => {
    if (window.history && window.history.length > 1) {
      window.history.back();
    } else {
      window.location.href = '/';
    }
  };

  const calculateMetrics = () => {
    const weeklyData = analytics.tickets_per_week || [];
    const totalTickets = weeklyData.reduce((sum, week) => sum + week.count, 0);
    const priorityData = analytics.priority_distribution || {};
    const typeData = analytics.type_distribution || {};

    const recentWeeks = weeklyData.slice(-4);
    let trend = 'stable';
    let trendValue = 0;

    if (recentWeeks.length >= 2) {
      const firstHalf = recentWeeks.slice(0, Math.floor(recentWeeks.length / 2));
      const secondHalf = recentWeeks.slice(Math.floor(recentWeeks.length / 2));

      const firstAvg = firstHalf.reduce((sum, w) => sum + w.count, 0) / firstHalf.length;
      const secondAvg = secondHalf.reduce((sum, w) => sum + w.count, 0) / secondHalf.length;

      if (firstAvg > 0) {
        trendValue = ((secondAvg - firstAvg) / firstAvg * 100);
        trend = trendValue > 10 ? 'up' : trendValue < -10 ? 'down' : 'stable';
      }
    }

    const highPriorityCount = (priorityData['High'] || 0) + (priorityData['Highest'] || 0) + (priorityData['Critical'] || 0);
    const mostCommonType = Object.keys(typeData).reduce((a, b) => 
      (typeData[a] || 0) > (typeData[b] || 0) ? a : b, Object.keys(typeData)[0] || 'N/A'
    );

    return {
      totalTickets,
      avgResolutionTime: analytics.avg_resolution_time || 0,
      highPriorityCount,
      mostCommonType,
      trend,
      trendValue: Math.abs(trendValue),
      weeklyAverage: totalTickets > 0 ? (totalTickets / Math.max(weeklyData.length, 1)) : 0
    };
  };

  const metrics = useMemo(() => calculateMetrics(), [analytics]);

  const KPICard = ({ title, value, subtitle, icon: Icon, trend, trendValue, color = "#667eea", unit = "" }) => {
    const trendIcon = trend === 'up' ? TrendingUp : trend === 'down' ? TrendingDown : BarChart3;
    const trendColor = trend === 'up' ? '#10B981' : trend === 'down' ? '#EF4444' : '#6B7280';
    const TrendIcon = trendIcon;

    return (
      <div 
        className="bg-gradient-to-br from-white to-gray-50 rounded-xl p-6 shadow-md border border-gray-200 transition-all duration-300 hover:-translate-y-1 hover:shadow-lg cursor-pointer relative overflow-hidden"
      >
        <div className="absolute top-0 left-0 right-0 h-1 bg-gradient-to-r from-{color} to-{color}99" />
        <div className="flex justify-between items-start mb-4">
          <div className="inline-flex p-3 rounded-xl bg-{color}/10">
            <Icon size={28} className={`text-${color}`} />
          </div>
          {trend && trendValue > 0 && (
            <div className="flex items-center gap-1 text-{trendColor} font-semibold text-sm">
              <TrendIcon size={14} />
              <span>{trendValue.toFixed(1)}%</span>
            </div>
          )}
        </div>
        <div>
          <h3 className="text-2xl font-bold text-gray-800 mb-2">{value}{unit}</h3>
          <p className="text-sm font-semibold text-gray-600 mb-1">{title}</p>
          {subtitle && <p className="text-xs text-gray-500">{subtitle}</p>}
        </div>
      </div>
    );
  };

  const DonutChart = ({ data, title, centerText }) => {
    if (!data || Object.keys(data).length === 0) {
      return (
        <div className="bg-gradient-to-br from-white to-gray-50 rounded-xl p-6 shadow-md border border-gray-200">
          <h3 className="text-xl font-semibold text-gray-800 text-center mb-6">{title}</h3>
          <div className="flex items-center justify-center h-80 text-gray-400 text-center">
            <div>
              <PieChart size={48} className="opacity-30 mb-2" />
              <p>No data available</p>
            </div>
          </div>
        </div>
      );
    }

    const chartData = Object.entries(data).map(([name, value]) => ({ name, value }));
    return (
      <div className="bg-gradient-to-br from-white to-gray-50 rounded-xl p-6 shadow-md border border-gray-200">
        <h3 className="text-xl font-semibold text-gray-800 text-center mb-6">{title}</h3>
        <ResponsiveContainer width="100%" height={320}>
          <RechartsPieChart>
            <Pie
              data={chartData}
              cx="50%"
              cy="50%"
              innerRadius={60}
              outerRadius={100}
              fill="#8884d8"
              dataKey="value"
              labelLine={false}
              label={({ name, percent }) => `${name} ${(percent * 100).toFixed(1)}%`}
            >
              {chartData.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
            <Tooltip formatter={(value) => `${value} tickets`} />
            <Legend />
          </RechartsPieChart>
        </ResponsiveContainer>
        <div className="text-center mt-4">
          <p className="text-2xl font-bold text-gray-800">{chartData.reduce((sum, { value }) => sum + value, 0)}</p>
          <p className="text-sm text-gray-500">{centerText}</p>
        </div>
      </div>
    );
  };

  const WeeklyTrendChart = ({ data }) => {
    if (!data || data.length === 0) {
      return (
        <div className="bg-gradient-to-br from-white to-gray-50 rounded-xl p-6 shadow-md border border-gray-200">
          <h3 className="text-xl font-semibold text-gray-800 text-center mb-6">Weekly Ticket Creation Trend</h3>
          <div className="flex items-center justify-center h-80 text-gray-400 text-center">
            <div>
              <BarChart3 size={48} className="opacity-30 mb-2" />
              <p>No data available</p>
            </div>
          </div>
        </div>
      );
    }

    const chartData = data.map(item => ({ week: item.week, count: item.count }));
    return (
      <div className="bg-gradient-to-br from-white to-gray-50 rounded-xl p-6 shadow-md border border-gray-200">
        <h3 className="text-xl font-semibold text-gray-800 text-center mb-6">Weekly Ticket Creation Trend</h3>
        <ResponsiveContainer width="100%" height={320}>
          <BarChart data={chartData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="week" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="count" fill="#8884d8" />
          </BarChart>
        </ResponsiveContainer>
      </div>
    );
  };

  const ScatterResolutionChart = ({ data }) => {
    const scatterData = analytics.tickets_per_week.map((week, index) => ({
      week: week.week,
      resolutionTime: analytics.avg_resolution_time,
      priority: Object.keys(analytics.priority_distribution || {}).length > 0 ? 
        Object.keys(analytics.priority_distribution)[index % Object.keys(analytics.priority_distribution).length] : 'Unknown'
    }));
    return (
      <div className="bg-gradient-to-br from-white to-gray-50 rounded-xl p-6 shadow-md border border-gray-200">
        <h3 className="text-xl font-semibold text-gray-800 text-center mb-6">Resolution Time vs Priority</h3>
        <ResponsiveContainer width="100%" height={320}>
          <ScatterChart>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis type="category" dataKey="week" name="Week" />
            <YAxis type="number" dataKey="resolutionTime" name="Resolution Time (days)" />
            <Tooltip cursor={{ strokeDasharray: '3 3' }} />
            <Legend />
            <Scatter name="Resolution" data={scatterData} fill="#8884d8">
              {scatterData.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
              ))}
            </Scatter>
          </ScatterChart>
        </ResponsiveContainer>
      </div>
    );
  };

  const RadialProgressChart = ({ data }) => {
    const progressData = [
      { name: 'Completed', value: (metrics.totalTickets > 0 ? (metrics.totalTickets - metrics.highPriorityCount) / metrics.totalTickets * 100 : 0), fill: '#10B981' },
      { name: 'High Priority', value: (metrics.totalTickets > 0 ? metrics.highPriorityCount / metrics.totalTickets * 100 : 0), fill: '#EF4444' }
    ].filter(d => d.value > 0);
    return (
      <div className="bg-gradient-to-br from-white to-gray-50 rounded-xl p-6 shadow-md border border-gray-200">
        <h3 className="text-xl font-semibold text-gray-800 text-center mb-6">Ticket Progress</h3>
        <ResponsiveContainer width="100%" height={320}>
          <RadialBarChart cx="50%" cy="50%" innerRadius="20%" outerRadius="80%" data={progressData}>
            <RadialBar label={{ position: 'insideStart', fill: '#fff' }} background dataKey="value" />
            <Tooltip />
            <Legend />
          </RadialBarChart>
        </ResponsiveContainer>
      </div>
    );
  };

  const InsightsPanel = () => {
    const insights = [
      {
        icon: TrendingUp,
        title: "Workload Trend",
        description: `${metrics.trend === 'up' ? 'Increasing' : metrics.trend === 'down' ? 'Decreasing' : 'Stable'} ticket volume over recent weeks`,
        color: metrics.trend === 'up' ? '#F59E0B' : metrics.trend === 'down' ? '#10B981' : '#6B7280',
      },
      {
        icon: AlertCircle,
        title: "Priority Focus",
        description: `${metrics.highPriorityCount} high-priority tickets require immediate attention`,
        color: metrics.highPriorityCount > 0 ? '#EF4444' : '#10B981',
      },
      {
        icon: Clock,
        title: "Resolution Efficiency",
        description: `Average ${metrics.avgResolutionTime.toFixed(1)} days resolution time`,
        color: metrics.avgResolutionTime <= 3 ? '#10B981' : metrics.avgResolutionTime <= 7 ? '#F59E0B' : '#EF4444',
      },
      {
        icon: PieChart,
        title: "Issue Pattern",
        description: `${metrics.mostCommonType} is the most frequent issue type`,
        color: '#8B5CF6',
      }
    ];

    return (
      <div className="bg-gradient-to-br from-white to-gray-50 rounded-xl p-6 shadow-md border border-gray-200">
        <h3 className="text-xl font-semibold text-gray-800 text-center mb-6">Key Insights</h3>
        <div className="grid gap-4">
          {insights.map((insight, index) => {
            const Icon = insight.icon;
            return (
              <div key={index} className="p-5 bg-gray-50 rounded-lg border-l-4 border-{insight.color} transition-all duration-300 hover:-translate-y-1 hover:shadow-lg">
                <div className="flex items-start gap-4">
                  <div className="p-3 rounded-xl bg-{insight.color}/10">
                    <Icon size={24} className={`text-${insight.color}`} />
                  </div>
                  <div className="flex-1">
                    <h4 className="font-semibold text-gray-800 mb-2">{insight.title}</h4>
                    <p className="text-sm text-gray-600">{insight.description}</p>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-600 to-purple-600 flex items-center justify-center font-sans">
        <div className="bg-white/95 rounded-2xl p-10 max-w-md text-center shadow-xl backdrop-blur-md">
          <div className="w-16 h-16 border-4 border-gray-300 border-t-blue-600 rounded-full animate-spin mx-auto mb-5" />
          <h3 className="text-2xl font-bold text-gray-800 mb-2">Loading Analytics</h3>
          <p className="text-gray-600">Fetching insights from your Jira data...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-600 to-purple-600 flex items-center justify-center p-4 font-sans">
        <div className="bg-white/95 rounded-2xl p-10 max-w-md text-center shadow-xl backdrop-blur-md">
          <AlertCircle size={64} className="text-red-500 mb-6" />
          <h3 className="text-2xl font-bold text-gray-800 mb-3">Analytics Error</h3>
          <p className="text-gray-600 mb-6">{error}</p>
          <div className="flex gap-4 justify-center">
            <button
              onClick={fetchAnalytics}
              className="flex items-center gap-2 px-6 py-3 bg-blue-600 text-white rounded-lg font-semibold hover:-translate-y-1 hover:shadow-lg transition-all duration-300"
            >
              <RefreshCw size={18} /> Retry
            </button>
            <button
              onClick={handleBackToMain}
              className="flex items-center gap-2 px-6 py-3 bg-gray-600 text-white rounded-lg font-semibold hover:-translate-y-1 hover:shadow-lg transition-all duration-300"
            >
              <ArrowLeft size={18} /> Back to Main
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 to-purple-600 font-sans">
      <style>{`
        @keyframes spin { 0% { transform: rotate(0deg); } 100% { transform: rotate(360deg); } }
        @keyframes fadeIn { 0% { opacity: 0; transform: translateY(20px); } 100% { opacity: 1; transform: translateY(0); } }
      `}</style>

      <div className="bg-white/95 backdrop-blur-md border-b border-white/20 shadow">
        <div className="max-w-6xl mx-auto p-6">
          <div className="flex flex-col md:flex-row justify-between items-center gap-4">
            <div className="flex items-center gap-6">
              <button
                onClick={handleBackToMain}
                className="p-3 bg-gradient-to-br from-white to-gray-50 rounded-xl border border-gray-200 hover:-translate-y-1 hover:shadow-md transition-all duration-300"
              >
                <ArrowLeft size={22} className="text-gray-600" />
              </button>
              <div>
                <h1 className="text-3xl font-bold text-transparent bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text">
                  Analytics Dashboard
                </h1>
                <p className="text-gray-600 font-medium">Comprehensive insights into your Jira workflow</p>
              </div>
            </div>
            <div className="flex items-center gap-4">
              <button
                onClick={fetchAnalytics}
                className="p-3 bg-gradient-to-br from-white to-gray-50 rounded-xl border border-gray-200 hover:-translate-y-1 hover:shadow-md transition-all duration-300"
              >
                <RefreshCw size={20} className="text-gray-600" />
              </button>
              <div className="bg-gradient-to-br from-white to-gray-50 rounded-xl p-1 border border-gray-200 shadow">
                <button
                  onClick={() => setActiveView('overview')}
                  className={`px-5 py-3 rounded-lg font-semibold transition-all duration-300 ${activeView === 'overview' ? 'bg-gradient-to-r from-blue-600 to-purple-600 text-white' : 'text-gray-600'}`}
                >
                  Overview
                </button>
                <button
                  onClick={() => setActiveView('trends')}
                  className={`px-5 py-3 rounded-lg font-semibold transition-all duration-300 ${activeView === 'trends' ? 'bg-gradient-to-r from-blue-600 to-purple-600 text-white' : 'text-gray-600'}`}
                >
                  Trends
                </button>
              </div>
              <select
                onChange={(e) => setTimeFilter(e.target.value)}
                className="p-3 rounded-lg border border-gray-200 bg-white text-gray-600 font-semibold hover:-translate-y-1 hover:shadow-md transition-all duration-300"
              >
                <option value="all">All Time</option>
                <option value="week">Last Week</option>
                <option value="month">Last Month</option>
              </select>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-6xl mx-auto p-8">
        {activeView === 'overview' ? (
          <div className="grid gap-8 animate-fadeIn">
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              <KPICard
                title="Total Tickets"
                value={metrics.totalTickets}
                subtitle="All time"
                icon={CheckCircle}
                trend={metrics.trend}
                trendValue={metrics.trendValue}
                color="#667eea"
              />
              <KPICard
                title="High Priority"
                value={metrics.highPriorityCount}
                subtitle="Requires attention"
                icon={AlertCircle}
                color="#EF4444"
              />
              <KPICard
                title="Avg Resolution"
                value={metrics.avgResolutionTime.toFixed(1)}
                subtitle="Days to resolve"
                icon={Clock}
                color="#10B981"
                unit=" days"
              />
              <KPICard
                title="Weekly Average"
                value={metrics.weeklyAverage.toFixed(1)}
                subtitle="Tickets per week"
                icon={Calendar}
                color="#8B5CF6"
              />
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              <DonutChart data={analytics.priority_distribution} title="Priority Distribution" centerText="Total" />
              <DonutChart data={analytics.type_distribution} title="Issue Type Distribution" centerText="Total" />
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              <WeeklyTrendChart data={analytics.tickets_per_week} />
              <InsightsPanel />
            </div>
          </div>
        ) : (
          <div className="grid gap-8 animate-fadeIn">
            <div className="bg-gradient-to-br from-white to-gray-50 rounded-xl p-8 shadow-md border border-gray-200">
              <h2 className="text-2xl font-bold text-gray-800 text-center mb-8">Trend Analysis</h2>
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                <div>
                  <h3 className="text-lg font-semibold text-gray-700 mb-6">Recent Performance</h3>
                  <div className="grid gap-4">
                    {analytics.tickets_per_week?.slice(-6).map((week, index) => (
                      <div key={index} className="flex justify-between items-center p-5 bg-gray-50 rounded-lg border border-gray-200 transition-all duration-300 hover:-translate-y-1 hover:shadow-md">
                        <span className="text-sm font-semibold text-gray-700">{week.week}</span>
                        <div className="text-right">
                          <div className="text-lg font-bold text-blue-600">{week.count}</div>
                          <div className="text-xs text-gray-500 font-medium">tickets created</div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
                <div>
                  <h3 className="text-lg font-semibold text-gray-700 mb-6">Performance Metrics</h3>
                  <div className="grid gap-5">
                    <div className="p-6 bg-blue-50 rounded-lg border-l-4 border-blue-500">
                      <div className="flex items-center gap-3 mb-3">
                        <TrendingUp size={24} className="text-blue-500" />
                        <span className="text-sm font-semibold text-blue-800">Volume Trend</span>
                      </div>
                      <p className="text-sm text-blue-700">
                        {metrics.trend === 'up' ? 'Increasing workload detected' : 
                         metrics.trend === 'down' ? 'Workload is decreasing' : 
                         'Steady workflow maintained'}
                      </p>
                    </div>
                    <div className="p-6 bg-green-50 rounded-lg border-l-4 border-green-500">
                      <div className="flex items-center gap-3 mb-3">
                        <Clock size={24} className="text-green-500" />
                        <span className="text-sm font-semibold text-green-800">Efficiency</span>
                      </div>
                      <p className="text-sm text-green-700">
                        {metrics.avgResolutionTime <= 3 ? 'Excellent resolution time' :
                         metrics.avgResolutionTime <= 7 ? 'Good resolution time' :
                         'Consider optimizing resolution process'}
                      </p>
                    </div>
                    <div className="p-6 bg-purple-50 rounded-lg border-l-4 border-purple-500">
                      <div className="flex items-center gap-3 mb-3">
                        <PieChart size={24} className="text-purple-500" />
                        <span className="text-sm font-semibold text-purple-800">Focus Area</span>
                      </div>
                      <p className="text-sm text-purple-700">
                        {metrics.mostCommonType} tickets are most common - consider process optimization
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              <ScatterResolutionChart data={analytics.tickets_per_week} />
              <RadialProgressChart data={analytics.tickets_per_week} />
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
