import React, { useState, useEffect } from 'react';
import { BarChart3, TrendingUp, Clock, Users, AlertCircle, CheckCircle, XCircle, Activity, Calendar, Target, PieChart, LineChart } from 'lucide-react';

export default function AnalyticsDashboard() {
  const [analytics, setAnalytics] = useState({
    tickets_per_week: [],
    priority_distribution: {},
    type_distribution: {},
    avg_resolution_time: 0.0,
    status_distribution: {},
    assignee_workload: {},
    velocity_metrics: {},
    trend_data: []
  });
  const [tickets, setTickets] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeView, setActiveView] = useState('overview');

  // Enhanced analytics calculation
  const calculateEnhancedAnalytics = (ticketData) => {
    const allTickets = [];
    const statusCounts = {};
    const assigneeWorkload = {};
    const priorityCounts = {};
    const typeCounts = {};

    // Process ticket data
    Object.entries(ticketData).forEach(([status, ticketList]) => {
      statusCounts[status] = ticketList.length;

      ticketList.forEach(ticketString => {
        const ticketInfo = parseTicketInfo(ticketString);
        if (ticketInfo) {
          allTickets.push({ ...ticketInfo, status });

          // Count by assignee
          const assignee = ticketInfo.assignee || 'Unassigned';
          assigneeWorkload[assignee] = (assigneeWorkload[assignee] || 0) + 1;

          // Count by priority
          const priority = ticketInfo.priority || 'None';
          priorityCounts[priority] = (priorityCounts[priority] || 0) + 1;

          // Count by type
          const type = ticketInfo.issue_type || 'Unknown';
          typeCounts[type] = (typeCounts[type] || 0) + 1;
        }
      });
    });

    // Generate mock time-series data for demonstration
    const generateWeeklyData = () => {
      const weeks = [];
      const currentDate = new Date();
      for (let i = 11; i >= 0; i--) {
        const weekDate = new Date(currentDate);
        weekDate.setDate(weekDate.getDate() - (i * 7));
        const weekNum = Math.floor(weekDate.getTime() / (7 * 24 * 60 * 60 * 1000));
        weeks.push({
          week: `${weekDate.getFullYear()}-${String(Math.floor(weekDate.getMonth() / 3) + 1).padStart(2, '0')}`,
          created: Math.floor(Math.random() * 8) + 1,
          resolved: Math.floor(Math.random() * 6) + 1,
          date: weekDate.toISOString().split('T')[0]
        });
      }
      return weeks;
    };

    return {
      total_tickets: allTickets.length,
      status_distribution: statusCounts,
      assignee_workload: assigneeWorkload,
      priority_distribution: priorityCounts,
      type_distribution: typeCounts,
      avg_resolution_time: 3.2, // Mock data
      weekly_trends: generateWeeklyData(),
      completion_rate: Object.keys(statusCounts).includes('Terminé(e)') 
        ? Math.round((statusCounts['Terminé(e)'] / allTickets.length) * 100) 
        : 0,
      active_tickets: allTickets.length - (statusCounts['Terminé(e)'] || 0)
    };
  };

  const parseTicketInfo = (ticketString) => {
    try {
      const parts = ticketString.split(': ', 1);
      if (parts.length < 1) return null;

      const key = parts[0].strip();
      const rest = ticketString.substring(key.length + 2);

      const bracketMatches = rest.match(/\[([^\]]+)\]/g);
      if (!bracketMatches || bracketMatches.length < 3) return null;

      const assignee = bracketMatches[0].slice(1, -1);
      const issue_type = bracketMatches[1].slice(1, -1);
      const priority = bracketMatches[2].slice(1, -1);

      const summaryMatch = rest.match(/^(.+?)\s*\[/);
      const summary = summaryMatch ? summaryMatch[1].trim() : rest.trim();

      return {
        key,
        summary,
        assignee: assignee !== 'Unassigned' ? assignee : null,
        issue_type,
        priority
      };
    } catch (error) {
      return null;
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch tickets data
        const ticketsResponse = await fetch('http://localhost:5000/api/tickets', {
          method: 'GET',
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
          },
          mode: 'cors'
        });

        if (!ticketsResponse.ok) {
          throw new Error(`HTTP ${ticketsResponse.status}: ${ticketsResponse.statusText}`);
        }

        const ticketsData = await ticketsResponse.json();
        setTickets(ticketsData);

        // Calculate enhanced analytics
        const enhancedAnalytics = calculateEnhancedAnalytics(ticketsData);
        setAnalytics(enhancedAnalytics);

      } catch (err) {
        console.error('Analytics fetch error:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Professional Card Component
  const MetricCard = ({ title, value, change, changeType, icon: Icon, color = 'blue' }) => {
    const colors = {
      blue: { bg: 'bg-blue-50', border: 'border-blue-200', icon: 'text-blue-600', text: 'text-blue-900' },
      green: { bg: 'bg-green-50', border: 'border-green-200', icon: 'text-green-600', text: 'text-green-900' },
      orange: { bg: 'bg-orange-50', border: 'border-orange-200', icon: 'text-orange-600', text: 'text-orange-900' },
      red: { bg: 'bg-red-50', border: 'border-red-200', icon: 'text-red-600', text: 'text-red-900' },
      purple: { bg: 'bg-purple-50', border: 'border-purple-200', icon: 'text-purple-600', text: 'text-purple-900' }
    };

    const colorClasses = colors[color];

    return (
      <div className={`${colorClasses.bg} ${colorClasses.border} border rounded-xl p-6 shadow-sm hover:shadow-md transition-shadow duration-200`}>
        <div className="flex items-center justify-between">
          <div>
            <p className="text-sm font-medium text-gray-600 mb-1">{title}</p>
            <p className={`text-3xl font-bold ${colorClasses.text} mb-2`}>{value}</p>
            {change && (
              <div className="flex items-center">
                <span className={`text-xs px-2 py-1 rounded-full ${
                  changeType === 'positive' ? 'bg-green-100 text-green-700' : 
                  changeType === 'negative' ? 'bg-red-100 text-red-700' : 
                  'bg-gray-100 text-gray-700'
                }`}>
                  {change}
                </span>
              </div>
            )}
          </div>
          <div className={`p-3 rounded-lg bg-white ${colorClasses.icon}`}>
            <Icon size={24} />
          </div>
        </div>
      </div>
    );
  };

  // Professional Chart Components
  const TrendChart = ({ data, title }) => {
    if (!data || data.length === 0) return <div className="text-center text-gray-500 py-8">No trend data available</div>;

    const maxValue = Math.max(...data.flatMap(d => [d.created, d.resolved]));
    const chartHeight = 200;

    return (
      <div className="bg-white rounded-xl border border-gray-200 p-6 shadow-sm">
        <div className="flex items-center justify-between mb-6">
          <h3 className="text-lg font-semibold text-gray-900">{title}</h3>
          <div className="flex items-center space-x-4 text-sm">
            <div className="flex items-center">
              <div className="w-3 h-3 bg-blue-500 rounded-full mr-2"></div>
              <span className="text-gray-600">Created</span>
            </div>
            <div className="flex items-center">
              <div className="w-3 h-3 bg-green-500 rounded-full mr-2"></div>
              <span className="text-gray-600">Resolved</span>
            </div>
          </div>
        </div>

        <div className="relative" style={{ height: chartHeight + 40 }}>
          <svg width="100%" height={chartHeight + 40} className="overflow-visible">
            {/* Grid lines */}
            {[0, 0.25, 0.5, 0.75, 1].map((ratio, i) => (
              <g key={i}>
                <line
                  x1="40"
                  y1={chartHeight * ratio + 10}
                  x2="100%"
                  y2={chartHeight * ratio + 10}
                  stroke="#e5e7eb"
                  strokeWidth="1"
                />
                <text
                  x="35"
                  y={chartHeight * ratio + 15}
                  textAnchor="end"
                  className="text-xs fill-gray-500"
                >
                  {Math.round(maxValue * (1 - ratio))}
                </text>
              </g>
            ))}

            {/* Data points and lines */}
            {data.map((point, index) => {
              const x = 50 + (index * (window.innerWidth * 0.8 - 100) / (data.length - 1));
              const createdY = 10 + chartHeight - (point.created / maxValue * chartHeight);
              const resolvedY = 10 + chartHeight - (point.resolved / maxValue * chartHeight);

              return (
                <g key={index}>
                  {/* Created line */}
                  {index > 0 && (
                    <line
                      x1={50 + ((index - 1) * (window.innerWidth * 0.8 - 100) / (data.length - 1))}
                      y1={10 + chartHeight - (data[index - 1].created / maxValue * chartHeight)}
                      x2={x}
                      y2={createdY}
                      stroke="#3b82f6"
                      strokeWidth="2"
                    />
                  )}

                  {/* Resolved line */}
                  {index > 0 && (
                    <line
                      x1={50 + ((index - 1) * (window.innerWidth * 0.8 - 100) / (data.length - 1))}
                      y1={10 + chartHeight - (data[index - 1].resolved / maxValue * chartHeight)}
                      x2={x}
                      y2={resolvedY}
                      stroke="#10b981"
                      strokeWidth="2"
                    />
                  )}

                  {/* Data points */}
                  <circle cx={x} cy={createdY} r="4" fill="#3b82f6" />
                  <circle cx={x} cy={resolvedY} r="4" fill="#10b981" />

                  {/* Week labels */}
                  <text
                    x={x}
                    y={chartHeight + 30}
                    textAnchor="middle"
                    className="text-xs fill-gray-500"
                  >
                    {point.week}
                  </text>
                </g>
              );
            })}
          </svg>
        </div>
      </div>
    );
  };

  const DonutChart = ({ data, title, colorScheme }) => {
    if (!data || Object.keys(data).length === 0) return <div className="text-center text-gray-500 py-8">No data available</div>;

    const entries = Object.entries(data);
    const total = entries.reduce((sum, [_, value]) => sum + value, 0);
    const colors = colorScheme || ['#3b82f6', '#10b981', '#f59e0b', '#ef4444', '#8b5cf6', '#06b6d4'];

    let cumulativeAngle = 0;
    const radius = 80;
    const innerRadius = 45;
    const centerX = 120;
    const centerY = 120;

    return (
      <div className="bg-white rounded-xl border border-gray-200 p-6 shadow-sm">
        <h3 className="text-lg font-semibold text-gray-900 mb-6">{title}</h3>
        <div className="flex items-center justify-between">
          <div className="relative">
            <svg width="240" height="240" className="overflow-visible">
              {/* Donut segments */}
              {entries.map(([name, value], index) => {
                const percentage = (value / total) * 100;
                const angle = (value / total) * 360;
                const startAngle = cumulativeAngle;
                const endAngle = cumulativeAngle + angle;

                const startRad = (startAngle * Math.PI) / 180;
                const endRad = (endAngle * Math.PI) / 180;

                const x1Outer = centerX + radius * Math.cos(startRad);
                const y1Outer = centerY + radius * Math.sin(startRad);
                const x2Outer = centerX + radius * Math.cos(endRad);
                const y2Outer = centerY + radius * Math.sin(endRad);

                const x1Inner = centerX + innerRadius * Math.cos(startRad);
                const y1Inner = centerY + innerRadius * Math.sin(startRad);
                const x2Inner = centerX + innerRadius * Math.cos(endRad);
                const y2Inner = centerY + innerRadius * Math.sin(endRad);

                const largeArcFlag = angle > 180 ? 1 : 0;

                const pathData = [
                  `M ${x1Outer} ${y1Outer}`,
                  `A ${radius} ${radius} 0 ${largeArcFlag} 1 ${x2Outer} ${y2Outer}`,
                  `L ${x2Inner} ${y2Inner}`,
                  `A ${innerRadius} ${innerRadius} 0 ${largeArcFlag} 0 ${x1Inner} ${y1Inner}`,
                  'Z'
                ].join(' ');

                cumulativeAngle += angle;

                return (
                  <path
                    key={index}
                    d={pathData}
                    fill={colors[index % colors.length]}
                    stroke="white"
                    strokeWidth="2"
                    className="hover:opacity-80 transition-opacity duration-200"
                  />
                );
              })}

              {/* Center text */}
              <text x={centerX} y={centerY - 5} textAnchor="middle" className="text-2xl font-bold fill-gray-700">
                {total}
              </text>
              <text x={centerX} y={centerY + 15} textAnchor="middle" className="text-sm fill-gray-500">
                Total
              </text>
            </svg>
          </div>

          <div className="ml-6 flex-1">
            <div className="space-y-3">
              {entries.map(([name, value], index) => {
                const percentage = ((value / total) * 100).toFixed(1);
                return (
                  <div key={index} className="flex items-center justify-between">
                    <div className="flex items-center">
                      <div 
                        className="w-3 h-3 rounded-full mr-3"
                        style={{ backgroundColor: colors[index % colors.length] }}
                      />
                      <span className="text-sm font-medium text-gray-700">{name}</span>
                    </div>
                    <div className="text-right">
                      <div className="text-sm font-semibold text-gray-900">{value}</div>
                      <div className="text-xs text-gray-500">{percentage}%</div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    );
  };

  const WorkloadChart = ({ data, title }) => {
    if (!data || Object.keys(data).length === 0) return <div className="text-center text-gray-500 py-8">No workload data available</div>;

    const entries = Object.entries(data).sort(([,a], [,b]) => b - a).slice(0, 8);
    const maxValue = Math.max(...entries.map(([_, value]) => value));

    return (
      <div className="bg-white rounded-xl border border-gray-200 p-6 shadow-sm">
        <h3 className="text-lg font-semibold text-gray-900 mb-6">{title}</h3>
        <div className="space-y-4">
          {entries.map(([assignee, count], index) => {
            const percentage = (count / maxValue) * 100;
            return (
              <div key={index} className="space-y-2">
                <div className="flex justify-between items-center">
                  <span className="text-sm font-medium text-gray-700 truncate">
                    {assignee === 'Unassigned' ? '🔒 Unassigned' : `👤 ${assignee}`}
                  </span>
                  <span className="text-sm font-semibold text-gray-900">{count}</span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div 
                    className="bg-gradient-to-r from-blue-500 to-blue-600 h-2 rounded-full transition-all duration-500 ease-out"
                    style={{ width: `${percentage}%` }}
                  />
                </div>
              </div>
            );
          })}
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <h2 className="text-xl font-semibold text-gray-700">Loading Analytics</h2>
          <p className="text-gray-500 mt-2">Processing ticket data...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="bg-white rounded-lg shadow-lg p-8 max-w-md mx-auto text-center">
          <XCircle className="h-16 w-16 text-red-500 mx-auto mb-4" />
          <h2 className="text-xl font-semibold text-gray-900 mb-2">Analytics Error</h2>
          <p className="text-gray-600 mb-6">{error}</p>
          <button 
            onClick={() => window.location.reload()}
            className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded-lg font-medium transition-colors duration-200"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 mb-8">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">Analytics Dashboard</h1>
              <p className="text-gray-600 mt-1">Comprehensive insights into your Jira project performance</p>
            </div>
            <div className="flex items-center space-x-4">
              <div className="flex bg-gray-100 rounded-lg p-1">
                <button
                  onClick={() => setActiveView('overview')}
                  className={`px-4 py-2 rounded-md text-sm font-medium transition-colors duration-200 ${
                    activeView === 'overview' ? 'bg-white text-blue-600 shadow-sm' : 'text-gray-600 hover:text-gray-900'
                  }`}
                >
                  Overview
                </button>
                <button
                  onClick={() => setActiveView('trends')}
                  className={`px-4 py-2 rounded-md text-sm font-medium transition-colors duration-200 ${
                    activeView === 'trends' ? 'bg-white text-blue-600 shadow-sm' : 'text-gray-600 hover:text-gray-900'
                  }`}
                >
                  Trends
                </button>
              </div>
              <button 
                onClick={() => window.history.back()}
                className="inline-flex items-center px-4 py-2 border border-gray-300 rounded-lg text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 transition-colors duration-200"
              >
                ← Back
              </button>
            </div>
          </div>
        </div>

        {activeView === 'overview' && (
          <>
            {/* Key Metrics */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
              <MetricCard
                title="Total Tickets"
                value={analytics.total_tickets}
                change="+12% from last month"
                changeType="positive"
                icon={Activity}
                color="blue"
              />
              <MetricCard
                title="Active Tickets"
                value={analytics.active_tickets}
                change={`${analytics.completion_rate}% completed`}
                changeType="neutral"
                icon={Clock}
                color="orange"
              />
              <MetricCard
                title="Completion Rate"
                value={`${analytics.completion_rate}%`}
                change="+5% this week"
                changeType="positive"
                icon={CheckCircle}
                color="green"
              />
              <MetricCard
                title="Avg Resolution"
                value={`${analytics.avg_resolution_time} days`}
                change="-0.5 days improved"
                changeType="positive"
                icon={TrendingUp}
                color="purple"
              />
            </div>

            {/* Charts Grid */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
              <DonutChart
                data={analytics.status_distribution}
                title="Status Distribution"
                colorScheme={['#3b82f6', '#10b981', '#f59e0b', '#ef4444']}
              />
              <DonutChart
                data={analytics.priority_distribution}
                title="Priority Breakdown"
                colorScheme={['#ef4444', '#f59e0b', '#10b981', '#6b7280']}
              />
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              <DonutChart
                data={analytics.type_distribution}
                title="Issue Type Distribution"
                colorScheme={['#8b5cf6', '#06b6d4', '#10b981', '#f59e0b']}
              />
              <WorkloadChart
                data={analytics.assignee_workload}
                title="Team Workload"
              />
            </div>
          </>
        )}

        {activeView === 'trends' && (
          <div className="space-y-8">
            <TrendChart
              data={analytics.weekly_trends}
              title="Weekly Ticket Trends"
            />

            {/* Additional trend metrics */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="bg-white rounded-xl border border-gray-200 p-6 text-center">
                <Calendar className="h-8 w-8 text-blue-600 mx-auto mb-3" />
                <h3 className="text-lg font-semibold text-gray-900 mb-2">This Week</h3>
                <p className="text-3xl font-bold text-blue-600">
                  {analytics.weekly_trends?.slice(-1)[0]?.created || 0}
                </p>
                <p className="text-sm text-gray-500">Tickets Created</p>
              </div>

              <div className="bg-white rounded-xl border border-gray-200 p-6 text-center">
                <Target className="h-8 w-8 text-green-600 mx-auto mb-3" />
                <h3 className="text-lg font-semibold text-gray-900 mb-2">Resolved</h3>
                <p className="text-3xl font-bold text-green-600">
                  {analytics.weekly_trends?.slice(-1)[0]?.resolved || 0}
                </p>
                <p className="text-sm text-gray-500">This Week</p>
              </div>

              <div className="bg-white rounded-xl border border-gray-200 p-6 text-center">
                <TrendingUp className="h-8 w-8 text-purple-600 mx-auto mb-3" />
                <h3 className="text-lg font-semibold text-gray-900 mb-2">Velocity</h3>
                <p className="text-3xl font-bold text-purple-600">
                  {(analytics.weekly_trends?.slice(-4).reduce((sum, week) => sum + week.resolved, 0) / 4).toFixed(1)}
                </p>
                <p className="text-sm text-gray-500">Avg/Week (4w)</p>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
