import React, { useState, useEffect } from 'react';
import { ArrowLeft, TrendingUp, TrendingDown, Clock, CheckCircle, AlertCircle, PieChart, BarChart3, Calendar, Users, RefreshCw } from 'lucide-react';

export default function ProfessionalAnalyticsDashboard() {
  const [analytics, setAnalytics] = useState({
    tickets_per_week: [],
    priority_distribution: {},
    type_distribution: {},
    avg_resolution_time: 0.0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeView, setActiveView] = useState('overview');

  useEffect(() => {
    fetchAnalytics();
  }, []);

  const fetchAnalytics = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:5000/api/analytics', {
        method: 'GET',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
        },
        mode: 'cors'
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();
      setAnalytics(data);

    } catch (err) {
      console.error('Analytics fetch error:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleBackToMain = () => {
    // Navigate back to main page
    if (window.history && window.history.length > 1) {
      window.history.back();
    } else {
      window.location.href = '/';
    }
  };

  const calculateMetrics = () => {
    const weeklyData = analytics.tickets_per_week || [];
    const totalTickets = weeklyData.reduce((sum, week) => sum + week.count, 0);
    const priorityData = analytics.priority_distribution || {};
    const typeData = analytics.type_distribution || {};

    // Calculate trend from last 4 weeks
    const recentWeeks = weeklyData.slice(-4);
    let trend = 'stable';
    let trendValue = 0;

    if (recentWeeks.length >= 2) {
      const firstHalf = recentWeeks.slice(0, Math.floor(recentWeeks.length / 2));
      const secondHalf = recentWeeks.slice(Math.floor(recentWeeks.length / 2));

      const firstAvg = firstHalf.reduce((sum, w) => sum + w.count, 0) / firstHalf.length;
      const secondAvg = secondHalf.reduce((sum, w) => sum + w.count, 0) / secondHalf.length;

      if (firstAvg > 0) {
        trendValue = ((secondAvg - firstAvg) / firstAvg * 100);
        trend = trendValue > 10 ? 'up' : trendValue < -10 ? 'down' : 'stable';
      }
    }

    // High priority tickets count
    const highPriorityCount = (priorityData['High'] || 0) + (priorityData['Highest'] || 0) + (priorityData['Critical'] || 0);

    // Most common issue type
    const mostCommonType = Object.keys(typeData).reduce((a, b) => 
      (typeData[a] || 0) > (typeData[b] || 0) ? a : b, Object.keys(typeData)[0] || 'N/A'
    );

    return {
      totalTickets,
      avgResolutionTime: analytics.avg_resolution_time || 0,
      highPriorityCount,
      mostCommonType,
      trend,
      trendValue: Math.abs(trendValue),
      weeklyAverage: totalTickets > 0 ? (totalTickets / Math.max(weeklyData.length, 1)) : 0
    };
  };

  const metrics = calculateMetrics();

  // Professional KPI Card Component with better styling
  const KPICard = ({ title, value, subtitle, icon: Icon, trend, trendValue, color = "#4F46E5", unit = "" }) => {
    const trendIcon = trend === 'up' ? TrendingUp : trend === 'down' ? TrendingDown : BarChart3;
    const trendColor = trend === 'up' ? '#10B981' : trend === 'down' ? '#EF4444' : '#6B7280';
    const TrendIcon = trendIcon;

    return (
      <div className="bg-white rounded-xl p-6 shadow-lg border-l-4 hover:shadow-xl transition-all duration-300" 
           style={{ borderLeftColor: color }}>
        <div className="flex justify-between items-start mb-4">
          <div 
            className="p-3 rounded-full"
            style={{ backgroundColor: `${color}20` }}
          >
            <Icon 
              size={28} 
              style={{ color: color }} 
            />
          </div>
          {trend && trendValue > 0 && (
            <div 
              className="flex items-center gap-1 px-2 py-1 rounded-full text-xs font-bold"
              style={{ 
                color: trendColor,
                backgroundColor: `${trendColor}20`
              }}
            >
              <TrendIcon size={14} />
              <span>{trendValue.toFixed(1)}%</span>
            </div>
          )}
        </div>

        <div>
          <h3 className="text-3xl font-bold text-gray-900 mb-2">
            {value}{unit}
          </h3>
          <p className="text-sm font-semibold text-gray-700 mb-1">
            {title}
          </p>
          {subtitle && (
            <p className="text-xs text-gray-500">
              {subtitle}
            </p>
          )}
        </div>
      </div>
    );
  };

  // Enhanced Donut Chart with better visual design
  const DonutChart = ({ data, title, centerText }) => {
    if (!data || Object.keys(data).length === 0) {
      return (
        <div className="bg-white rounded-xl p-6 shadow-lg border border-gray-100">
          <h3 className="text-xl font-bold text-gray-900 mb-6 text-center">{title}</h3>
          <div className="flex items-center justify-center h-80 text-gray-400">
            <div className="text-center">
              <PieChart size={48} className="mx-auto mb-2 opacity-30" />
              <p>No data available</p>
            </div>
          </div>
        </div>
      );
    }

    const entries = Object.entries(data);
    const total = entries.reduce((sum, [_, value]) => sum + value, 0);
    const colors = ['#4F46E5', '#10B981', '#F59E0B', '#EF4444', '#8B5CF6', '#EC4899', '#14B8A6', '#F97316'];

    let cumulativeAngle = 0;
    const radius = 90;
    const innerRadius = 55;
    const centerX = 130;
    const centerY = 130;

    return (
      <div className="bg-white rounded-xl p-6 shadow-lg border border-gray-100">
        <h3 className="text-xl font-bold text-gray-900 mb-6 text-center">{title}</h3>

        <div className="flex items-center justify-between gap-8">
          <div className="relative">
            <svg width="260" height="260" className="drop-shadow-sm">
              {entries.map(([name, value], index) => {
                const percentage = (value / total) * 100;
                const angle = (value / total) * 360;
                const startAngle = cumulativeAngle;
                const endAngle = cumulativeAngle + angle;

                const startRad = (startAngle * Math.PI) / 180;
                const endRad = (endAngle * Math.PI) / 180;

                const x1Outer = centerX + radius * Math.cos(startRad);
                const y1Outer = centerY + radius * Math.sin(startRad);
                const x2Outer = centerX + radius * Math.cos(endRad);
                const y2Outer = centerY + radius * Math.sin(endRad);

                const x1Inner = centerX + innerRadius * Math.cos(startRad);
                const y1Inner = centerY + innerRadius * Math.sin(startRad);
                const x2Inner = centerX + innerRadius * Math.cos(endRad);
                const y2Inner = centerY + innerRadius * Math.sin(endRad);

                const largeArcFlag = angle > 180 ? 1 : 0;

                const pathData = [
                  `M ${x1Inner} ${y1Inner}`,
                  `L ${x1Outer} ${y1Outer}`,
                  `A ${radius} ${radius} 0 ${largeArcFlag} 1 ${x2Outer} ${y2Outer}`,
                  `L ${x2Inner} ${y2Inner}`,
                  `A ${innerRadius} ${innerRadius} 0 ${largeArcFlag} 0 ${x1Inner} ${y1Inner}`,
                  'Z'
                ].join(' ');

                cumulativeAngle += angle;

                return (
                  <path
                    key={index}
                    d={pathData}
                    fill={colors[index % colors.length]}
                    stroke="#fff"
                    strokeWidth="3"
                    className="hover:opacity-80 transition-opacity cursor-pointer hover:scale-105"
                    style={{ 
                      filter: 'drop-shadow(0 2px 8px rgba(0,0,0,0.15))',
                      transformOrigin: `${centerX}px ${centerY}px`
                    }}
                  >
                    <title>{`${name}: ${value} (${percentage.toFixed(1)}%)`}</title>
                  </path>
                );
              })}
            </svg>

            <div className="absolute inset-0 flex flex-col items-center justify-center">
              <div className="text-3xl font-bold text-gray-900">
                {total}
              </div>
              <div className="text-sm text-gray-600 font-medium">
                {centerText}
              </div>
            </div>
          </div>

          <div className="flex-1 min-w-0">
            <div className="space-y-3 max-h-80 overflow-y-auto">
              {entries.map(([name, value], index) => {
                const percentage = ((value / total) * 100).toFixed(1);
                return (
                  <div key={index} className="flex items-center justify-between p-4 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors border">
                    <div className="flex items-center gap-3">
                      <div 
                        className="w-4 h-4 rounded-full shadow-sm"
                        style={{ backgroundColor: colors[index % colors.length] }}
                      />
                      <span className="text-sm font-semibold text-gray-900">{name}</span>
                    </div>
                    <div className="text-right">
                      <div className="text-lg font-bold text-gray-900">{value}</div>
                      <div className="text-xs text-gray-500">{percentage}%</div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    );
  };

  // Enhanced Weekly Trend Chart
  const WeeklyTrendChart = ({ data, title }) => {
    if (!data || data.length === 0) {
      return (
        <div className="bg-white rounded-xl p-6 shadow-lg border border-gray-100">
          <h3 className="text-xl font-bold text-gray-900 mb-6 text-center">{title}</h3>
          <div className="flex items-center justify-center h-80 text-gray-400">
            <div className="text-center">
              <BarChart3 size={48} className="mx-auto mb-2 opacity-30" />
              <p>No data available</p>
            </div>
          </div>
        </div>
      );
    }

    const maxValue = Math.max(...data.map(item => item.count));
    const recentData = data.slice(-8);

    return (
      <div className="bg-white rounded-xl p-6 shadow-lg border border-gray-100">
        <h3 className="text-xl font-bold text-gray-900 mb-6 text-center">{title}</h3>

        <div className="space-y-4 max-h-96 overflow-y-auto">
          {recentData.map((item, index) => {
            const percentage = maxValue > 0 ? (item.count / maxValue) * 100 : 0;
            const isHighest = item.count === maxValue;

            return (
              <div key={index} className={`p-5 rounded-lg transition-all duration-300 hover:shadow-md ${
                isHighest ? 'bg-indigo-50 border-2 border-indigo-200' : 'bg-gray-50 border border-gray-200'
              }`}>
                <div className="flex justify-between items-center mb-3">
                  <span className="text-sm font-bold text-gray-700">{item.week}</span>
                  <div className="flex items-center gap-2">
                    <span className={`text-xl font-bold ${isHighest ? 'text-indigo-700' : 'text-indigo-600'}`}>
                      {item.count}
                    </span>
                    {isHighest && <span className="text-xs bg-indigo-200 text-indigo-800 px-2 py-1 rounded-full font-semibold">Peak</span>}
                  </div>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-3 overflow-hidden">
                  <div 
                    className={`h-3 rounded-full transition-all duration-1000 ease-out ${
                      isHighest 
                        ? 'bg-gradient-to-r from-indigo-600 to-purple-600' 
                        : 'bg-gradient-to-r from-indigo-500 to-indigo-600'
                    }`}
                    style={{ width: `${percentage}%` }}
                  />
                </div>
              </div>
            );
          })}
        </div>
      </div>
    );
  };

  // Enhanced Insights Panel
  const InsightsPanel = () => {
    const insights = [
      {
        icon: TrendingUp,
        title: "Workload Trend",
        description: `${metrics.trend === 'up' ? 'Increasing' : metrics.trend === 'down' ? 'Decreasing' : 'Stable'} ticket volume over recent weeks`,
        color: metrics.trend === 'up' ? '#F59E0B' : metrics.trend === 'down' ? '#10B981' : '#6B7280',
        status: metrics.trend === 'up' ? 'warning' : metrics.trend === 'down' ? 'success' : 'neutral'
      },
      {
        icon: AlertCircle,
        title: "Priority Focus",
        description: `${metrics.highPriorityCount} high-priority tickets require immediate attention`,
        color: metrics.highPriorityCount > 0 ? '#EF4444' : '#10B981',
        status: metrics.highPriorityCount > 0 ? 'critical' : 'success'
      },
      {
        icon: Clock,
        title: "Resolution Efficiency",
        description: `Average ${metrics.avgResolutionTime.toFixed(1)} days resolution time`,
        color: metrics.avgResolutionTime <= 3 ? '#10B981' : metrics.avgResolutionTime <= 7 ? '#F59E0B' : '#EF4444',
        status: metrics.avgResolutionTime <= 3 ? 'excellent' : metrics.avgResolutionTime <= 7 ? 'good' : 'needs improvement'
      },
      {
        icon: PieChart,
        title: "Issue Pattern",
        description: `${metrics.mostCommonType} is the most frequent issue type`,
        color: '#8B5CF6',
        status: 'info'
      }
    ];

    return (
      <div className="bg-white rounded-xl p-6 shadow-lg border border-gray-100">
        <h3 className="text-xl font-bold text-gray-900 mb-6 text-center">Key Insights</h3>
        <div className="space-y-4">
          {insights.map((insight, index) => {
            const Icon = insight.icon;
            return (
              <div key={index} className="p-5 border-l-4 rounded-lg hover:shadow-md transition-all duration-300 bg-gradient-to-r from-gray-50 to-white" 
                   style={{ borderLeftColor: insight.color }}>
                <div className="flex items-start gap-4">
                  <div 
                    className="p-3 rounded-lg shadow-sm"
                    style={{ backgroundColor: `${insight.color}20` }}
                  >
                    <Icon size={24} style={{ color: insight.color }} />
                  </div>
                  <div className="flex-1">
                    <div className="flex items-center justify-between mb-2">
                      <h4 className="font-bold text-gray-900">{insight.title}</h4>
                      <span className={`px-2 py-1 text-xs font-semibold rounded-full ${
                        insight.status === 'critical' ? 'bg-red-100 text-red-800' :
                        insight.status === 'warning' ? 'bg-yellow-100 text-yellow-800' :
                        insight.status === 'success' ? 'bg-green-100 text-green-800' :
                        insight.status === 'excellent' ? 'bg-emerald-100 text-emerald-800' :
                        'bg-gray-100 text-gray-800'
                      }`}>
                        {insight.status}
                      </span>
                    </div>
                    <p className="text-sm text-gray-600 leading-relaxed">{insight.description}</p>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center p-8 bg-white rounded-xl shadow-xl max-w-md w-full">
          <div className="w-16 h-16 border-4 border-indigo-200 border-t-indigo-600 rounded-full animate-spin mx-auto mb-6" />
          <h3 className="text-2xl font-bold text-gray-900 mb-3">Loading Analytics</h3>
          <p className="text-gray-600">Fetching insights from your Jira data...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center p-4">
        <div className="bg-white rounded-xl p-8 max-w-md w-full text-center shadow-xl">
          <AlertCircle size={64} className="text-red-500 mx-auto mb-6" />
          <h3 className="text-2xl font-bold text-gray-900 mb-3">Analytics Error</h3>
          <p className="text-gray-600 mb-8">{error}</p>
          <div className="flex gap-3 justify-center">
            <button 
              onClick={fetchAnalytics}
              className="flex items-center gap-2 px-6 py-3 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition-colors shadow-md"
            >
              <RefreshCw size={18} />
              Retry
            </button>
            <button 
              onClick={handleBackToMain}
              className="flex items-center gap-2 px-6 py-3 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors shadow-md"
            >
              <ArrowLeft size={18} />
              Back to Main
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Enhanced Header */}
      <div className="bg-white shadow-lg border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-6 py-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-6">
              <button 
                onClick={handleBackToMain}
                className="p-3 hover:bg-gray-100 rounded-xl transition-colors shadow-sm border border-gray-200"
                title="Back to Jira Manager Pro"
              >
                <ArrowLeft size={22} className="text-gray-600" />
              </button>
              <div>
                <h1 className="text-3xl font-bold text-gray-900 mb-1">Analytics Dashboard</h1>
                <p className="text-gray-600 font-medium">Comprehensive insights into your Jira workflow</p>
              </div>
            </div>

            {/* Enhanced View Toggle */}
            <div className="flex items-center gap-4">
              <button 
                onClick={fetchAnalytics}
                className="p-3 hover:bg-gray-100 rounded-xl transition-colors shadow-sm border border-gray-200"
                title="Refresh Data"
              >
                <RefreshCw size={20} className="text-gray-600" />
              </button>

              <div className="flex items-center gap-2 bg-gray-100 rounded-xl p-1 shadow-sm">
                <button
                  onClick={() => setActiveView('overview')}
                  className={`px-6 py-3 rounded-lg text-sm font-semibold transition-all ${
                    activeView === 'overview' 
                      ? 'bg-white text-indigo-700 shadow-md' 
                      : 'text-gray-600 hover:text-gray-900 hover:bg-gray-50'
                  }`}
                >
                  Overview
                </button>
                <button
                  onClick={() => setActiveView('trends')}
                  className={`px-6 py-3 rounded-lg text-sm font-semibold transition-all ${
                    activeView === 'trends' 
                      ? 'bg-white text-indigo-700 shadow-md' 
                      : 'text-gray-600 hover:text-gray-900 hover:bg-gray-50'
                  }`}
                >
                  Trends
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-6 py-8">
        {activeView === 'overview' ? (
          <div className="space-y-8">
            {/* Enhanced KPI Cards */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              <KPICard
                title="Total Tickets"
                value={metrics.totalTickets}
                subtitle="All time"
                icon={CheckCircle}
                trend={metrics.trend}
                trendValue={metrics.trendValue}
                color="#4F46E5"
              />
              <KPICard
                title="High Priority"
                value={metrics.highPriorityCount}
                subtitle="Requires attention"
                icon={AlertCircle}
                color="#EF4444"
              />
              <KPICard
                title="Avg Resolution"
                value={metrics.avgResolutionTime.toFixed(1)}
                subtitle="Days to resolve"
                icon={Clock}
                color="#10B981"
                unit=" days"
              />
              <KPICard
                title="Weekly Average"
                value={metrics.weeklyAverage.toFixed(1)}
                subtitle="Tickets per week"
                icon={Calendar}
                color="#8B5CF6"
              />
            </div>

            {/* Enhanced Charts Grid */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              <DonutChart 
                data={analytics.priority_distribution} 
                title="Priority Distribution"
                centerText="Total"
              />
              <DonutChart 
                data={analytics.type_distribution} 
                title="Issue Type Distribution"
                centerText="Total"
              />
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
              <div className="lg:col-span-2">
                <WeeklyTrendChart 
                  data={analytics.tickets_per_week} 
                  title="Weekly Ticket Creation Trend"
                />
              </div>
              <div>
                <InsightsPanel />
              </div>
            </div>
          </div>
        ) : (
          <div className="space-y-8">
            <div className="bg-white rounded-xl p-8 shadow-lg border border-gray-100">
              <h2 className="text-3xl font-bold text-gray-900 mb-8 text-center">Trend Analysis</h2>

              <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                <div>
                  <h3 className="text-xl font-bold text-gray-900 mb-6">Recent Performance</h3>
                  <div className="space-y-4">
                    {analytics.tickets_per_week?.slice(-6).map((week, index) => (
                      <div key={index} className="flex items-center justify-between p-5 bg-gradient-to-r from-gray-50 to-gray-100 rounded-lg hover:shadow-md transition-all duration-300 border">
                        <span className="font-bold text-gray-900">{week.week}</span>
                        <div className="text-right">
                          <div className="text-xl font-bold text-indigo-600">{week.count}</div>
                          <div className="text-xs text-gray-500 font-medium">tickets created</div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                <div>
                  <h3 className="text-xl font-bold text-gray-900 mb-6">Performance Metrics</h3>
                  <div className="space-y-5">
                    <div className="p-6 bg-gradient-to-r from-indigo-50 to-indigo-100 rounded-xl border-l-4 border-indigo-500 shadow-sm">
                      <div className="flex items-center gap-3 mb-3">
                        <TrendingUp size={28} className="text-indigo-600" />
                        <span className="font-bold text-indigo-900 text-lg">Volume Trend</span>
                      </div>
                      <p className="text-indigo-800 font-medium">
                        {metrics.trend === 'up' ? 'Increasing workload detected' : 
                         metrics.trend === 'down' ? 'Workload is decreasing' : 
                         'Steady workflow maintained'}
                      </p>
                    </div>

                    <div className="p-6 bg-gradient-to-r from-green-50 to-emerald-50 rounded-xl border-l-4 border-green-500 shadow-sm">
                      <div className="flex items-center gap-3 mb-3">
                        <Clock size={28} className="text-green-600" />
                        <span className="font-bold text-green-900 text-lg">Efficiency</span>
                      </div>
                      <p className="text-green-800 font-medium">
                        {metrics.avgResolutionTime <= 3 ? 'Excellent resolution time' :
                         metrics.avgResolutionTime <= 7 ? 'Good resolution time' :
                         'Consider optimizing resolution process'}
                      </p>
                    </div>

                    <div className="p-6 bg-gradient-to-r from-purple-50 to-pink-50 rounded-xl border-l-4 border-purple-500 shadow-sm">
                      <div className="flex items-center gap-3 mb-3">
                        <PieChart size={28} className="text-purple-600" />
                        <span className="font-bold text-purple-900 text-lg">Focus Area</span>
                      </div>
                      <p className="text-purple-800 font-medium">
                        {metrics.mostCommonType} tickets are most common - consider process optimization
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
